    }
.cell img {
		border-radius: 5px 20px;
  		box-shadow: 0px 0px 5px gray inset;
  border-right: 2px solid gray;
  border-bottom: 2px solid gray;
}
.branches.grid-block.grid-holder.emerge {
display:none;
}
.user.grid-block {
float:right;
}
.av-header, .over-vda.grid-block, .side.grid-block {
