css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("mega.co.nz"),
domain("mega.nz"),
domain("mega") 
{

.st-mid-white-block,
.nw-bottom-block, 
.st-full-block,
.st-gray-block,
.main-pad-block,
.jspContainer,
.fm-empty-cloud-bg,
.fm-empty-cloud,
.fm-right-files-block,
.create-new-folder,
.fm-notifications-bottom,
.fm-dialog-header,
.top-login-popup,
.top-menu-popup,
.top-submenu-popup,
.fm-dialog-body,
.top-context-menu,
.context-menu
{
background-color: #000000 !important;
}
.download-mid-white-block,
.download-mid-centered-block, .jspPane td
{
background: #000000 !important;
}
.downloading-progress, .top-head,
.new-start-arrow-button, 
.login-register-input, input,
.registration-success,
.fm-notification-body,
.fm-dialog-button,
.top-login-input-block,
.fm-dialog-rename-button,
.properties-body,
.fm-tree-folder, 
.ads-left-block,
.ads-right-block
{
background: #D82D00 !important;
}
.st-apps-icon, .transfer-panel,
th
{
background-color: #D82D00 !important;
}
.downloading-progress-bar
{
background: #FFFFFF !important;
}
.fm-dialog-header
{
border-top: 2px #FFFFFF solid !important;
}
.fm-dialog-header
{
border-bottom: 2px #000000 solid !important;
}
.fm-notifications-bottom
{
border-bottom: 2px #FFFFFF solid !important;
}
.fm-dialog-header,
.fm-notifications-bottom,
.fm-notification-body,
.properties-body
{
border-left: 2px #FFFFFF solid !important;
border-right: 2px #FFFFFF solid !important;
}
.login-register-input, 
.create-new-folder,
.top-login-popup,
.top-menu-popup,
.top-submenu-popup,
.fm-dialog-body,
.context-menu
{
border: 2px #FFFFFF solid !important;
}
.downloading .new-download-icon div
{
color: #000000 !important;
}
a, p, td,
input, label,
h1, h2, h3, h4, h5, h6,
.new-download-file-title, .new-download-file-size,
div, .properties-body span, .ads-left-block,
.ads-right-block, .red
{
color: #FFFFFF !important;
}
}
