css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("trello.com") {
textarea,input {font-family:'b nazanin','bnazanin',tahoma !important;direction:rtl;}
body{font-family:'b nazanin','bnazanin',tahoma !important;}
.gutter li{
  direction:rtl;
  background: url("http://subtlepatterns.com/patterns/subtle_white_feathers.png") repeat !important;
  margin: 0 16px 16px 0;

}
.markeddown{
  direction:rtl;
}
.js-open-board{
  opacity:0.7;
  margin: 2px !important;
}
.board-list-item-name{
   color:#000;
}
#surface{
   background: url("http://subtlepatterns.com/patterns/sativa.png") repeat !important;   
}
.board-header a,.board-header span,.sidebar-show-btn span{
   color:#000 !important;
} 
#header{
background: #72aeea !important; /* Old browsers */
background: -moz-linear-gradient(top,  #72aeea 0%, #4096ee 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#72aeea), color-stop(100%,#4096ee)); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  #72aeea 0%,#4096ee 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #72aeea 0%,#4096ee 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #72aeea 0%,#4096ee 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  #72aeea 0%,#4096ee 100%) !important; /* W3C */
}
.list{
  direction:rtl;
}
}
