css:
@-moz-document regexp("https?://([a-z]+\\.)?astats\\.nl/Forum/.*") {
html,
html body {
  font-size: 13px;
  font-family: "Arial", "Helvetica", sans-serif;
}

html pre {
	font-family: "Source Code Pro", "Inconsolata", "Consolas", "Monaco", "Courier New", monospace;
}

html #menubar,
html h4,
html .postauthor,
html .postdetails,
html .nav,
html .genmed,
html .genmed2,
html .genmedwbg a,
html .gensmall,
html .gensmallw,
html .gensmallw a:link,
html .gensmallw a:visited,
html .copyright,
html td.profile,
html .row1 a,
html .row1 a:hover,
html .row1t a,
html .row1t a:hover,
html .row2 a,
html .row2 a:hover,
html .row2t a,
html .row2t a:hover,
html a.forumlink,
html a.topictitle,
html select,
html .btnlite,
html td.gen {
  font-size: 1em;
  /* font-size: 11px; */
}

html p,
html pre,
html .forumrules,
html .btnbbcode,
html .btnmain,
html .btnfile,
html input {
  font-size: 1em;
  /* font-size: 1.1em; */
}

html .gen {
  font-size: 1em;
  /* font-size: 1.2em; */
}

html .postbody,
html textarea {
  font-size: 1em;
  /* font-size: 1.3em; */
}

html .postdetails,
html .row1,
html .row1:hover,
html .row1t,
html .row1t:hover,
html .row2,
html .row2:hover,
html .row2t,
html .row2t:hover,
html .syntaxbg {
  color: white;
  /* color: #989b98; */
}

html a:link {
  color: #A0A0FF;
}
html a:visited {
  color: #C080FF;
}

html body,
html .forumrules,
html h1,
html h2,
html h3,
html h4,
html .postauthor,
html .postbody,
html .copyright,
html .titles,
html th,
html a.forumlink,
html a.topictitle,
html input,
html textarea,
html select,
html .btnbbcode,
html .quotecontent,
html .attachcontent,
html .codetitle,
html .codecontent {
	font-family: inherit;
}

}
