css:
@-moz-document domain('www.felisfire.com')    {

body {
background: url("http://oi59.tinypic.com/20tkv0w.jpg") #000 !important;
background-repeat:repeat !important;
}

#content::before {
background: url("http://i59.tinypic.com/hx4tcn.jpg") #161617 !important;
-webkit-box-shadow: 0 0 5px 5px #161617 !important;
box-shadow: 0 0 5px 5px #161617 !important;
}

#sidebar::before {
background: url("http://i59.tinypic.com/hx4tcn.jpg") #161617 !important;
-webkit-box-shadow: 0 0 5px 5px #161617 !important;
box-shadow: 0 0 5px 5px #161617 !important;
}

header > nav {
border: 1px solid #AFA0BD !important;
background: #3E2854 !important;
box-shadow: 0px 0px 2px 2px #AFA0BD !important;
}

::-moz-selection {
    background: #D9B3FC !important;
}

::selection {
    background: #D9B3FC !important;
}

.green {
  color:#714A8F !important;
}

#breadcrumbs > li::after {
  color:#714A8F !important;
}

#top-search::after {
  color: #714A8F !important;
}


.news-date {
  color:#714A8F !important;
}
  
footer {
  color:#714A8F !important;
}
  
input:-moz-placeholder,
textarea:-moz-placeholder {
  color: #714A8F !important;
}
    

input:-ms-input-placeholder,
textarea:-ms-input-placeholder {
  color: #714A8F !important;
}

input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
  color: #714A8F !important;
}
    

footer .green {
  color:#AFA0BD !important;
}
    
.btn {
  color:#fff !important;
  border: 1px solid #AFA0BD;
  border-radius: 30px;
  background:#3E2854;
  box-shadow: 0px 0px 2px 2px #AFA0BD;
}

.btn:hover,
.btn.active {
  background-color: #AFA0BD !important;
  color: #000 !important;
}

header > nav a:hover,
header > nav a.active {
  color:#AFA0BD !important;
}

#content-wrapper a {
  color:#AFA0BD !important;
}

.stats > li > span {
  color:#AFA0BD !important;
}

select
 {
  border: 2px solid #AFA0BD !important;
  background:#000 !important;
  box-shadow: 0px 0px 5px 0px #AFA0BD !important;
  color:#fff !important;
}


input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
textarea
 {
  border: 2px solid #AFA0BD !important;
  background:#000 !important;
  box-shadow: 0px 0px 5px 0px #AFA0BD !important;
}

#content-wrapper a:hover {
  color:#BA70FF !important;
}

span.label {
color: #BA70FF !important;
}

th {
color: #BA70FF !important;
background-color: #000000 !important;
}
.btn.disabled,
.btn.disabled:hover {
  color:#fff !important;
  background:#000 !important;
  border-color: #A4A2A6 !important;
  box-shadow: 0px 0px 16px #A4A2A6 !important;
}

#logo {
  background:url("http://i59.tinypic.com/hx4tcn.jpg") no-repeat !important;
}

header > nav a {
  color:#fff !important;
}

#sidebar { 
  color:#fff !important;  
}

#sidebar h2 {
  color: #fff !important;
}

#sidebar h3 {
  color:#fff !important;
}

#content {
  color:#fff !important;
}

.user-avatar {
  background: #000 !important;
  border: 3px solid #fff !important;
}
  
footer a {
  color:#fff !important;
}

footer a:hover {
  color:#fff !important;
}
    
#users-online {
  border:2px solid #2E1B3D !important;
  background:#160B1E !important;
}
    
.news h3 {
  border-bottom: 1px solid #23192A !important;
}
      
#social-links a.social-facebook {
  background:url("http://i59.tinypic.com/hx4tcn.jpg") no-repeat 0 0 !important;
}
#social-links a.social-twitter {
  background:url("http://i59.tinypic.com/hx4tcn.jpg") no-repeat 0 -80px !important;
}
#social-links a.social-tumblr {
  background:url("http://i59.tinypic.com/hx4tcn.jpg") no-repeat 0 -160px !important;
}
      

.navli {
	background-color: #000 !important;
}

table.hold td.color3, table.widehold td.color3, td.color3 {
background-color: #2B2133 !important;
border: 1px solid #221A29 !important;
}

table.hold td.color1, table.widehold td.color1, td.color1 {
border: 1px solid #221A29 !important;
}

table.hold td.color2, table.widehold td.color2, td.color2 {
border: 1px solid #221A29 !important;
}

#sidebar hr {
border-bottom: 1px solid #221A29 !important;
}

element.style {
border: 1px solid #221A29 !important;
}

td.breathe.color1 {
background-color: transparent !important;
}

td.breathe.color2 {
background-color: transparent !important;
}

th {
background-color: transparent !important;
}

#sidebar .machine {
background: url("http://oi61.tinypic.com/wgyiq0.jpg") no-repeat 50% !important;
}

#sidebar .orbs li {
background: url("http://oi62.tinypic.com/34s2o2w.jpg") no-repeat 0 0 !important;
}

header > nav > ul > li {
display: table-cell;
width: 16.666%;
text-align: center;
vertical-align: middle;
background: url(http://oi58.tinypic.com/2448t5h.jpg) no-repeat 100% 50%;
}
}
