css:
@-moz-document
	url-prefix("https://www.tumblr.com/") 
{
/* ======= Selector ======= */

.tab.iconic .selection_nipple {
  border: none;
  background: url("http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o2_100.png") top left no-repeat;
  width: 36px;
  height: 48px;
  margin-top: -12px;
  margin-left: -17px;
}

/*#popover_blogs .tab_notice:after{display:none; }*/

.tab_bar .tab.iconic {padding: 0 6.5px}
.tab.iconic > a.tab_anchor {
	background-image: url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o6_250.png');
	background-size: auto;
	width: 35px;
}

  #account_button .icon_user_settings::before,
  DIV.tab_bar > .tab.iconic > .tab_anchor::before { content: none }

  #account_button .icon_arrow_carrot_down {position: relative; right: -24px; }
  #account_button {margin-right: 12px}

  #home_button .tab_anchor { background-position: 0px 0; }
  #inbox_button .tab_anchor { background-position: -35px 0; }
  #xkit-control.xkit-help-forwarder > .tab_anchor,
  /*#help_button*/#activity_button .tab_anchor { background-position: -70px 0; }
  #account_button .tab_anchor { background-position: -105px 0; } /* ex #preferences_button */
  #discover_button .tab_anchor { background-position: -140px 0; } /* ex #logout_button */
}
