css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("t411.me"), url("http://www.t411.me"), url("https://www.t411.me"){
@font-face {
	font-family:'Tahoma';
	font-weight:normal;
	font-style:normal;
}

body{background:#444444}

a{
text-decoration:none;
outline:0;
color:#0487b8;
}

a:hover{
text-decoration:underline;
color:#fff;
}

.head .logo, .footer .logos {
	background:url('data:image/png;base64,') no-repeat center center;
}

#left, #right, .contentWrapper:first-child, .banner728 {display:none;}

.contentWrapper:first-child{background:#444444;}

.content {
	width:90%;
	margin:20px 0 0 5%;
	background:#6f6e6e;
}

.headWrapper {
	background:#303030;
	box-shadow:none;
}

#lavamenu {
	margin:35px 0 0 246px;
}

#lavamenu .backLava {opacity:0;display:none;}

#lavamenu li {
	border-radius:0;
	padding:5px 3px;
}

#lavamenu li a {
	font-family:'Tahoma';
	font-size:16px;
	color:#fff;
}

#lavamenu li:hover {background:#444444;}

#lavamenu li ul {
	background:#303030; /*444444*/
	margin:12px 0 0 -7px;
	min-width:150px;
	display:none;
	padding:0;
	border-radius:0;
	border:none;
}
#lavamenu li:hover ul {display:block;}

#lavamenu li ul li {
	padding:7px 10px;
	border:none;
}

#lavamenu li ul li:hover {background:rgba(255,255,255,0.05);}

.head .searchBar, .head .loginBar, .head .loginBarSide {
	background:transparent;
}

.head .searchBar {
	width:250px;
	height:35px;
	margin:0;
	overflow:hidden;
}

.head .toolbar, .loginWrapper {
	background:#303030;
}

.head .toolbar {height:35px;padding:1px 0 0 0;}
.head .toolbar .loginBar {padding:3px 0 0 10px;}

.headerPlace .loginWrapper {
	height:120px;
	margin:-120px 0 0 0;
	-webkit-transition:0.3s;
	-moz-transition:0.3s;
	-o-transition:0.3s;
	-ms-transition:0.3s;
	transition:0.3s;
}

.headerPlace.active .loginWrapper {margin:0;}

.loginBar span {font-family:Helvetica;font-weight:100;color:#fff;}
.loginBar span a {font-family:Helvetica;color:#fff;}
.loginBar .slideToo, .loginBar .slideToo.active {background:transparent;color:#fff;}
.loginBarSide {display:none;}

#bar-search *, #searchBar #bar-search .searchHistory {display:none;}

#bar-search input {
	background:rgba(255,255,255,0.05);
	top:-5px;
	margin:0;
	padding:7px;
	border-radius:0;
	outline:none;
	position:relative;
	display:inline-block;
	font-family:'Tahoma';
	font-size:14px;
	color:#fff;
}

.contentWrapper h2 {
	background:#303030;
	padding:7px;
	border:none;
	border-radius:0;
	box-shadow:none;
}

.contentWrapper h2 span {
	background:#303030;
	border:none;
	font-family:'Tahoma';
	font-weight:normal;
	color:#fff;
}

.footer {
	background:#303030;
	font-family:'Tahoma';
	font-weight:normal;
	color:#fff;
}

.footer .stats h4, .footer .stats dl dt, .footer .stats dl dd {
	font-family:'Tahoma';
	font-weight:normal;
	color:#fff;
}

.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all {
	background:#444444;
	padding:0;
	border:none;
	border-radius:0;
}

.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all li {
	padding:0;
	border:none;
}

.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all li a {
	background:rgba(255,255,255,0.1);
	padding:6px;
	border:none;
	border-radius:0;
	font-family:'Tahoma';
	font-weight:normal;
	color:#fff;
}

.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all li a.ui-state-hover {
	background:rgba(255,255,255,0.2);
	margin:0;
	border:none;
}

.userBox .avatar-big {
	border:1px solid rgba(255,255,255,0.1);
	border-radius:0;
}

.userBox .column li a {
	font-family:'Tahoma';
	font-weight:normal;
	color:#fff;
}

.content .btn, .content .redbtn, .terms-list a {
	background:#303030;
	padding:6px;
	border:none;
	border-radius:0;
	box-shadow:none;
	font-family:'Tahoma';
	font-weight:normal;
	color:#fff;
}

.content .btn, .content .redbtn:hover {text-decoration:none;}

#progress .green.ui-progressbar.ui-widget.ui-widget-content.ui-corner-all {
	border:1px solid rgb(180,180,180);
	border-radius:0;
}

#progressbar div:first-child {
	padding:2px 0;
	font-family:'Tahoma';
	font-size:15px;
	font-weight:normal;
	color:rgba(0,0,0,0.7);
}

#progressbar .ui-progressbar-value.ui-widget-header.ui-corner-left {
	background:#84C97B;
	border:1px solid #71A36A;;
	border-radius:0;
}

.loginBar span, .loginBar span strong {
	font-family:'Tahoma';
	font-size:13px;
	font-weight:normal;
	color:#fff;
}

.navigation {display:none;}

.ads, .bottomads, .details div:first-child {display:none;}

.block.votes a, .block.votes a span {
	background:none;
	width:auto;
	padding:0;
	margin:0;
	font-family:'Tahoma';
	font-size:15px;
	font-weight:normal;
	color:rgba(255,255,255,0.7); /*0,0,0,0.7*/
}

.comment th, .comment th a {
	padding:10px 10px 5px 10px;
	font-family:'Tahoma';
	font-size:15px;
	font-weight:normal;
	color:rgba(0,0,0,0.7);
	text-align:left;
}

.details table tbody td {
	background:rgb(230,230,230);
	border:1px solid #0487b8;
}

.details table tbody tr, .details table tbody tr:nth-child(7) a {color:rgba(0,0,0,0.7);}

.details table thead, .details table tfoot,
.details table tbody tr:last-child,
.details table tbody tr:nth-child(2),
.details table tbody tr:nth-child(3),
.details table tbody tr:nth-child(5) {display:none;}
}
