css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bild.me") {

/*BG*/
body {
    background-color: #000;
    color: #0a0;
}
/*hyperlinks*/
a {
    color: #090;
}
/*hover*/
a:hover, a:focus {
    color: rgb(0, 250, 0);
}
/*footer*/
#topmenu {
    background-color: #000;
    background: none;
    color: #555;
}

/*removed text*/
noscript > a {
    display: none;
}

/*buttons*/
input, select, textarea {
    line-height: 1;
    font-size: 12px;
    position: relative;
    padding: 2px;
    margin: 4px;
    border: 1px solid #0c0;
    color: #0f0;
}
/*logo*/
td > a > img {
    display: none;
}    
td > a > img :before{
    content: url('http://www.jesus-is-savior.com/False%20Religions/Illuminati/96.gif') !important;
    display: block;
    margin-left: auto;
    margin-right: auto;
    }
body > center:before{
    content: url('http://www.jesus-is-savior.com/False%20Religions/Illuminati/96.gif')!important; 
    }
td {
    display: none;
    }
center {
    display: none;
} 
/*textarea(links)*/
textarea {
    background-color: #000;
    color: #fff !important;
} 

body > a > img {
    display: none;
}
 
}
