css:
@-moz-document url-prefix("http://blog.fefe.de/"), url-prefix("https://blog.fefe.de/") {
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 300;
  src: local('Roboto Light'), local('Roboto-Light'), url(//themes.googleusercontent.com/static/fonts/roboto/v11/Hgo13k-tfSpn0qi1SFdUfT8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Regular'), local('Roboto-Regular'), url(//themes.googleusercontent.com/static/fonts/roboto/v11/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff');
}

html, body {
  background-color: #EEE;
  font-family: 'Roboto', sans-serif !important;
  text-align: center;
}

body {
  padding: 0px;
  margin: 0px;
  padding-bottom: 20px;
  margin-left: 10px;
  margin-right: 10px;
}

a {
  color: #000;
}

body > ul {
  padding-left: 0px;
}

ul {
  list-style: none;
  max-width: 800px;
  margin: 0 auto;
  padding-left: 0px;
}

ul > li {
  box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.26);
  padding: 15px;
  background-color: #FFF;
  margin-bottom: 20px;
  font-size: 0.8em;
  line-height: 1.5em;
  font-weight: 400;
  text-align: left;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}

ul > li > a:first-child {
  display: block;
  width: 100%;
  text-align: right;
  background-color: #00bcd4;
  padding-right: 15px;
  padding-left: 15px;
  padding-top: 8px;
  padding-bottom: 8px;
  margin-top: -15px;
  margin-left: -15px;
  margin-bottom: 10px;
  color: #FFF;
  text-decoration: none;
  font-size: 0.8em;
  -webkit-border-top-left-radius: 3px;
  -webkit-border-top-right-radius: 3px;
  -moz-border-radius-topleft: 3px;
  -moz-border-radius-topright: 3px;
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
}

ul > li > a:first-child::before {
  content: "Permalink ";
}

ul > li > a:first-child:hover {
  text-decoration: underline;
}

h3 {
  font-size: 1.5em;
  font-weight: 300;
  padding-top: 30px;
  max-width: 800px;
  display: block;
  text-align: left;
  margin: 0 auto;
  margin-bottom: 20px;
}

body > h2:first-of-type {
  padding-top: 55px;
  padding-bottom: 55px;
  background-color: #00bcd4;
  display: block;
  margin-top: 0px;
  margin: 0 auto;
  width: calc(100% + 20px);
  text-align: center;
  margin-bottom: 20px;
  text-decoration: none;
  color: #FFF !important;
  font-weight: 300;
  font-size: 4.5em;
  font-family: 'Roboto', sans-serif !important;
  margin-left: -10px;
}

@media all and (max-device-width: 720px){
  body > h2:first-of-type {
    font-size: 13vw;
    padding-top: 30px;
    padding-bottom: 30px;
  }
}

body > h2:first-of-type > a {
  text-decoration: none;
  color: #FFF !important;
  max-width: 800px;
  display: block;
  margin: 0 auto;
  text-align: left !important;
  padding-left: 10px;
}

body > b:first-of-type {
  font-size: 0.9em;
  font-weight: 300;
}

body > p:first-of-type {
  margin-bottom: -20px;
}

div:last-of-type {
  margin-top: 10px;
  font-size: 0.9em;
  max-width: 800px;
  margin: 0 auto;
  text-align: right;
  margin-bottom: 15px;
}

div:first-of-type {
  text-align: center;
  margin-bottom: 40px;
  margin-top: 40px;
}

div:first-of-type > a {
  background-color: #26c6da;
  color: #FFF;
  padding: 13px;
  text-decoration: none;
  text-transform: uppercase;
  font-size: 0.8em;
  box-shadow: 0 2px 5px rgba(0,0,0,.16);
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}

div:first-of-type > a:hover {
  box-shadow: 0 2px 5px rgba(0,0,0,.26);
  background-color: #00BCD4;
}

body > b:first-of-type {
  margin-top: 20px;
  max-width: 800px;
  display: block;
  margin: 0 auto;
  text-align: left;
  padding-top: 5px;
  color: #333;
}

body > p:first-of-type {
  max-width: 800px;
  display: block;
  margin: 0 auto;
  text-align: left !important;
  padding-top: 5px;
  margin-bottom: 0px !important;
  color: #333;
}

body > p:first-of-type a {
  color: #333;
}


body > p {
  max-width: 800px;
  display: block;
  margin: 0 auto;
  text-align: left;
}

ol {
  max-width: 800px;
  margin: 0 auto;
  padding-bottom: 20px;
}

ol li {
  text-align: left;
}

h3 > a:hover {
  background-color: initial !important;
}

form > input {
  border: #ccc 1px solid;
  height: 25px;
  padding-left: 10px;
  padding-right: 10px;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
  font-size: 1em;
}

form > input[type="submit"] {
  background-color: #00BCD4;
  color: #FFF;
  border: none;
  padding-left: 10px;
  padding-right: 10px;
  font-size: 1em;
  height: 29px;
}

form > input[type="submit"]:hover {
  box-shadow: 0 2px 5px rgba(0,0,0,.16);
  cursor: pointer;
}

form {
  margin-top: 10px;
  margin-bottom: 10px;
}

p > img {
  margin-top: 10px;
}
}
