css:
@-moz-document domain("habitrpg.com") {
/* TWEAKS */
.sticky-wrapper                { height:     173px !important; }
h2                             { font-size:   22px;            }
.task                          { min-height:  24px;            }
.task-meta-controls            { margin-top:   5px !important; }
label:not(:hover)::after       { margin-top:  10px !important; }
.current-pet                   { bottom:     -15px !important; }
.habits > ul > li > .task-text { padding-left: 2.75em;         }
.habit-wide .task-text         { padding-left: 5em !important; }
ul.items.rewards.ng-scope	   { padding-bottom: 40px;         }
[ng-controller='TasksCtrl']    { margin-top: 200px;            }
[ng-controller='HeaderCtrl']   { position: fixed;              }

/* HIDE STUFFS */
.toolbar-toggle,
.ng-scope.tabbable.tabs-below,
.completed, span.task-notes,
a[ng-click='pushTask(task,$index,"top")'],
[ng-click="collapseChecklist(task)"],
[ng-show="task.history"], hr,
[ng-show="task.streak"],
div.container-fluid.ng-scope {
  display: none !important;
}

.toolbar-nav, .toolbar-subscribe, .toolbar-options,
.avatar-level, .hero-stats, .bar {
  -webkit-filter: invert(80%);
  -moz-filter: invert(80%);
  -ms-filter: invert(80%);
  -o-filter: invert(80%);
  filter: invert(80%);
}

body {
  font-size: 12px;
  background-image: url('http://i.imgur.com/mxW5MlK.png');
}

[ng-controller=HeaderCtrl] {
  background-image: url('https://33.media.tumblr.com/tumblr_m3mt82g1KE1rv68y7o1_500.gif') !important;
  background-size: auto 100%;
  box-shadow: 5px 0px 15px grey;
}

.hero-stats,
.herobox,
a::after {
  background: none !important;
  border: none !important;
}

.toolbar {
  background-image: url('http://static.tumblr.com/dccf8b5988f0ef3d54a878b6d8e2767a/ds4gwqs/WVamns6dd/tumblr_static_ghost_pixel_background_by_pizzaxreader_copy.png') !important;
  border-color: black !important;
}
   
.task-text {
  padding-top: 5px;
  padding-bottom: 5px;
}

.task-action-btn,
label::before,
.markdown-img {
  max-height: 26px;
  max-width: 26px;
  line-height: 26px !important;
}
  
ul.rewards.main-list,
ul.items.rewards {
    border-bottom: none;
    padding-bottom: 0;
}
}
