css:
@-moz-document domain(feedly.com) {


div#feedlyFrame {
    background-image: url(https://lh5.googleusercontent.com/-iRLrtoSw0ds/VAhxcPibGyI/AAAAAAAAAoo/EQR12EZi4IM/s50-no/lightgreystripes.gif);
    border-bottom-width: 0;
    border-color: #d4d4d4;
    border-left-width: 1px;
    border-right-width: 0;
    border-style: solid;
    border-top-width: 0;
    color: #222;
}

div#feedlyTabs .label {
  font-family: PF BeauSans Pro, Tahoma;
  font-size: 15px;
}

div#feedlyTabs {
    background-color: #f5f5f3;
    color: #222222;
}

div#feedlyTabsHolder {
    background-color: rgba(0, 0, 0, 0.06);
    background-image: url(http://s3.feedly.com/production/head/images/icon-feedly.png );
}

#feedlyTitleBar {
    background-color: rgba(0, 0, 0, 0.02);
    border: 1px solid #D4D4D4;
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
    color: rgba( 0, 0, 0, 0.6 );
    font-family: PF BeauSans Pro, Tahoma;
    padding-bottom: 50px;
    padding-left: 15px;
    padding-top: 10px;
}

div#fixedProfile {
    background-color: #EFEFEF;
    border-color: rgba( 0, 0, 0, 0.06 );
    border-top-width: 1px;
}

div#floatingBar {
    background-color: #f5f5f3;
    border-bottom-width: 1px;
    border-color: #D4D4D4;
    border-left-width: 1px;
    border-right-width: 0;
    border-style: solid;
    border-top-width: 0;
}

div#herculeHint {
    background-color: rgba(0, 0, 0, 0.04);
    border-bottom-left-radius: 20px;
    border-bottom-width: 1px;
    border-left-width: 1px;
    border-right-width: 0;
    border-style: solid;
    border-top-right-radius: 20px;
    border-top-width: 0;
}

div#recommentationAreaHeadlines {
    background-color: rgba(0, 0, 0, 0.02);
    background-image: url(https://lh6.googleusercontent.com/-GjmbNLYhErs/VC5rCai8yCI/AAAAAAAAAww/Yir4U3Bqu5w/w170-h298-no/facebook.png);
    background-position: right center;
    background-repeat: no-repeat;
    border: 1px solid #D4D4D4;
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
    padding: 10px;
    transition-duration: 600ms;
}

div#recommentationAreaHeadlines:hover {
    background-image: url(https://lh4.googleusercontent.com/-ANNfPn__wQA/VC6x8z5WBzI/AAAAAAAAAx0/LkdoR0FPAOs/w170-h298-no/facebooks.png);
    background-position: right center;
    background-repeat: no-repeat;
}

div.dark #feedlyTabsHolder #latesttab_icon {
    background-image: url(http://s3.feedly.com/production/head/images/selector-latest.png );
}

div.dark #feedlyTabsHolder #mytab_icon {
    background-image: url(http://s3.feedly.com/production/head/images/selector-my.png );
}

div.dark #feedlyTabsHolder #savedtab_icon {
    background-image: url(http://s3.feedly.com/production/head/images/selector-saved.png );
}

div.dark #feedlyTabsHolder .handle {
    background-image: url(http://s3.feedly.com/production/head/images/selector-right-arrow.png );
}

.dark #feedlyTabsHolder .handle.expanded {
    background-image: url(http://s3.feedly.com/production/head/images/selector-down-arrow.png );
}

div.dark .pinButton {
    border: 1px solid rgba(0, 0, 0, 0.1);
    color: rgba(0, 0, 0, 0.35);
}

div.dark .profileMore {
    background-image: url(http://s3.feedly.com/production/head/images/ab-more.png );
}

div.entryBody a {
    border: none;
}

div.slideBumper {
    height: 5000px;
    margin-left: -114px;
    margin-top: -2500px;
    position: absolute;
    width: 210px;
}

div.title, a.feedTitle {
    font-family: PF BeauSans Pro, Tahoma;
    text-transform: none;
}

div.topRecommendedEntry .summary {
    height: 45px;
    margin-top: 2px;
    overflow: hidden;
    padding: 3px;
}

div.topRecommendedEntry .title {
    height: 33px;
    overflow: hidden;
    padding-left: 3px;
    padding-right: 3px;
    font-family: PF BeauSans Pro, Tahoma;
}

div.u4Entry .title, #floatingEntry div.u100Entry .title, div.u5Entry .title, .inlineFrame div.u100entry .title, div.u12Entry .title {
    font-family: PF BeauSans Pro, Tahoma;
}

div.u4Entry .visual {
    border-bottom-left-radius: 20px;
    border-top-left-radius: 20px;
}

a.sourceTitle {
    display: inline-block;
    float: inherit;
    margin-bottom: -5px;
    max-width: 130px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

a.title {
    overflow: hidden;
}

a.websiteCallForAction {
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
}

b {
    font-size: 17px;
}

div.content {
    color: #222222;
    font-family: PF BeauSans Pro, Tahoma;
    font-size: 17px;
}

div.content a:hover, span.feedTitle:hover, div.label.nonEmpty.emptyAware:hover {
    text-shadow: 0 0 0.1px;
}

div.content img, div.entryBody img {
    height: inherit;
    max-width: 100%;
    min-width: 650px;
}

div.endOfSectionAction {
    border: 1px solid #d4d4d4;
    border-radius: 20px;
    display: block;
    float: right;
    margin-top: 5px;
    padding: 1px 5px 1px 5px;
}

div.endOfSectionAction:hover {
    box-shadow: 0 0 20px #555555;
    text-decoration: none;
}

div.feedIndexTitleHolder.nonEmpty.emptyAware {
    font-family: PF BeauSans Pro, Tahoma;
    font-size: 13.4px;
}

div.floatingEntryContent-tab.tab_0 {
    background-image: url(https://lh4.googleusercontent.com/-02aRDLVXkUs/VAhxcFfpolI/AAAAAAAAAok/JeKw5H_btLo/w40-h58-no/tab-first.png);
}

div.floatingEntryOverlay {
    background-color: rgba(0, 0, 0, 0.7);
    background-image: linear-gradient(to left, #000000 -50%, transparent 100%);
    width: 18.7%;
}

div.floatingEntryScroller, div.floatingEntryContentSlide.sliderWidth, div.frameActionsTop, div.entryholder, div.u100Entry, tbody tr td, div.headerInfo.headerInfo-article, div.headerInfo.sliderContainer {
    background-color: #fbfbfb;
}

div.inlineFrame {
    background-color: #fbfbfb;
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
    margin-bottom: 10px;
    min-width: 650px;
}

div.label.nonEmpty.emptyAware {
    color: rgba( 0, 0, 0, 0.6 );
}

div.label.nonempty {
    color: rgba( 0, 0, 0, 0.6 );
    font-family: PF BeauSans Pro, Tahoma;
    font-size: 13.4px;
    margin-top: 6px;
}

div.label.primary.nonempty {
    color: rgba( 0, 0, 0, 0.6 );
}

div.metadata {
    padding-bottom: 5px;
    padding-left: 10px;
}

div.proShowcase {
    display: none;
}

div.secondaryCallForAction {
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
    margin: 0;
}

div.sliderContainer {
    min-width: 81.3%;
}

div.summary {
    color: #555555;
    font-family: PF BeauSans Pro, Tahoma;
    font-size: 12.9px;
    margin-bottom: 5px;
    margin-top: 5px;
}

div.topRecommendedEntry, div.topRecommendedEntry .visual {
    max-width: 195px;
}

div.topRecommendedEntry:hover {
    border-bottom-left-radius: 20px;
    border-bottom-right-radius: 20px;
    box-shadow: 0 0 20px #555555;
}

div.u0Entry {
    background: #fbfbfb;
    border-color: d4d4d4;
    border-radius: 20px;
    border-style: solid;
    border-width: 1px;
    box-shadow: 0 0 20px #cccccc;
    font-family: PF BeauSans Pro, Tahoma;
    font-size: 13.4px;
    margin-bottom: 5px;
    padding-right: 5px;
}

div.u0Entry .title {
    font-size: 15px;
}

div.u100Entry {
    width: 650px;
}

div.u100Frame {
    background-color: #fbfbfb;
    border-color: #D4D4D4;
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
}

div.u100Frame.selectedEntry {
    border-color: #A0A0A0;
    border-radius: 20px;
}

div.u12Entry {
    background-image: url(https://lh4.googleusercontent.com/-wEiCuMSjJ_Q/VC-gSfOQJDI/AAAAAAAAAzM/CHLC4Jn4gRY/w190-h35-no/toch.png);
    background-position: left center;
    background-repeat: no-repeat;
    border: 1px solid #d4d4d4;
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
    margin: 3px;
    padding: 5px;
    transition-duration: 300ms;
}

div.u12Entry .title {
    display: block;
    height: 16px;
    max-width: 95%;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

div.u4Entry {
    background-color: fbfbfb;
    border: 1px solid #d4d4d4;
    border-radius: 20px;
    box-shadow: 0 0 20px #cccccc;
    margin-bottom: 10px;
    padding: 5px;
}

div.u4Entry .title {
    display: block;
    height: 33px;
    max-width: 95%;
    overflow: hidden;
}

div.u4Entry .title, div.u5Entry .title {
    font-size: 17px;
}

div.u5Entry {
    border-bottom-left-radius: 20px;
    border-bottom-right-radius: 20px;
    box-shadow: 0 0 20px #bbbbbb;
}

div.youtubePlaceholder {
    padding-left: 34px;
}

div:hover.U5Entry, div:hover.u0Entry, div:hover.u4Entry, div:hover.u12Entry {
    box-shadow: 0 0 20px #555555;
}

div:hover.secondaryCallForAction, a:hover.websiteCallForAction {
    background-color: transparent;
    box-shadow: 0 0 20px #555555;
}

div:hover.u12Entry {
    background-image: url(https://lh6.googleusercontent.com/-u9POh_1QZhU/VC-iAZpgrdI/AAAAAAAAAzg/IibKRSIzHKw/w190-h35-no/toch2.png);
    background-position: 170 center;
    background-repeat: no-repeat;
}

embed, body iframe, .full-frame, div.youtubePlaceholder, div.u100Frame {
    min-height: 396px;
    min-width: 650px;
}

h2 {
    color: #555555;
}

span.feedTitle {
    color: rgba( 0, 0, 0, 0.6 );
}

span.nbrRecommendations {
    color: 333333;
    font-size: 13.4px;
    font-style: italic;
    font-weight: bold;
}

span.u0Summary {
    font-size: 13.4px;
}

div.content a {
    color: #222277;
    transition-duration: 300ms;
}

div.content h2 {
    font-family: PF BeauSans Pro, Tahoma;
}
}
