css:
/* @namespace url(http://www.w3.org/1999/xhtml); */

@-moz-document domain("www.vvschaesberg.nl") {

* {
    box-shadow: none !important;
    border-radius: 0 !important;
    
}

body {
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-position-y: 5% !important;
}

/*No-border*/.wrapper #container > .kolom.een #HomeDiv .blok ul li, .wrapper #container > .kolom.twee #InfoBalk #NieuwsItem_NieuwsItem.item ul li, .wrapper #container > .kolom.twee #InfoBalk #UitslagenEnStanden_Stand.item #IbStand li, .wrapper #menubalk ul li a, .wrapper #container > .kolom.een .blok {
    border: unset;
}

/*Header*/
    .wrapper #header .stramienAfb {
        background-repeat: no-repeat;
        background-position-x: center; 
        background-position-y: center;
        background-size: cover; 
    }

    .wrapper #header .stramienAfb:not(:hover) {
        opacity: .7;
    }
/*Header*/

.wrapper #menubalk .search {
    background: rgba(245, 3, 3, .7);
}

/*Content*/
    #HomeDiv .row {
        margin-top: -19px;
    }

    .wrapper #container > .kolom.een .blok h2 {
        height: 65.6px;
        background: url("http://www.vvschaesberg.nl//Skins/Default4/Images/infobalk_h2_bg.ashx?hex=f50303");
        border-bottom: 4px solid black;
        color: white;
    }

    .wrapper #container > .kolom.een .blok h2, .wrapper #container > .kolom.twee #InfoBalk .item h2 {
        opacity: .7;
    }

    wrapper #container > .kolom.een .blok {
        background: rgba(255, 255, 255, .7);
    }
/*Content*/

.wrapper #container > .kolom.een .blok, .wrapper #container > .kolom.twee #InfoBalk .item {
    background: linear-gradient(transparent 65.6px, rgba(255, 255, 255, .7 ) 65.6px) !important;
}
/*Footer*/
    #footer .grijs, #footer .boog {
        background: rgba(77, 77, 77, .7);
    }

    #footer .socialmedia, #footer .vaFooter {
        background-color: rgba(245, 3, 3, .7);
    }
/*Footer*/

}
