css:
@-moz-document domain("checkvist.com") {
dir, .task, .editable, .selectedTask {line-height: 1em !important; min-height: 1em !important; font-size: 13px !important}
#h1Id {font-weight: bold !important;}
.dialog {left: 0px !important; top: 0px !important; width: 100px !important}
.content, h1, table, .dialog, body, .main_div, .dir, .frame, .task, .editable, .selectedTask, div.coreDiv {margin: 0px !important; padding: 0px !important}
ul .editable {border-top: 1px dashed silver; width: 100%; }
/*hide toolbar*/
.topbar, #toolbar {display: none}
/*setting for ul padding-left*/
ul.topLevel ul.dndUl {padding-left: 5px !important;}
ul.topLevel {padding-left: 10px;}
.frame, div.nodeImage {left: -9px !important; width: 7px !important;}
.notice {top: 0px !important;}
#tasks_block {min-width: 5em; width: 100% !important;}
}
