css:
@-moz-document domain("wiki.garrysmod.com") {
navigation {
	z-index: 99999;
}
div#column-content {
	overflow: hidden;
	padding-left: 50px;
}
span.client,
span.server,
span.shared {
	width: auto;
}
span.client:after,
span.server:after,
span.shared:after {
	color: white;
	font-weight: bold;
	padding-right: 7px;
	margin-right: 0px;
}
span.client:after {
	content: "Client";
}
span.server:after {
	content: "Server";
}
span.shared:after {
	content: "Shared";
}
}
