css:
@-moz-document domain( "docs.oracle.com" ){
  body{
    background-color: #080808 ; /*Very dark gray*/
    color: #CCC ; /*Light gray*/
  }
  
  hr.clearfloat{
    color: black ;
    background-color: black ;
  }
  
  h1{
    color: blueviolet ;
  }
  
  h2, h3, h4, h5{
    color: darkmagenta ;
  }
  
  noscript{
    display: none ;
    position: fixed ;
    top: -100px ;
  }
  
  #TopBar_left{
    color: deepskyblue ;
  }
  
  .subNav{
    background-color: #1A232D ;
  }
  
  .topNav, .bottomNav {
    background-color: #001827 ;
    color: #CCC ;
  }
  
  .block {
    color: #D1D1D1 ;
  }
  
  .altColor {
    background-color: #121212 ;
  }
  
  .rowColor {
    background-color: #000 ;
  }
  
  .title{
    color:#00FF00 ;
  }
  
  .contentContainer .description dl dt, .contentContainer .details dl dt, .serializedFormContainer dl dt{
    color: #B0450B ;
  }
  
  ul.blockList ul.blockList ul.blockList li.blockList, ul.blockList ul.blockList ul.blockListLast li.blockList {
    background-color: #1D1D1D ;
}
  
  th.colFirst, th.colLast, th.colOne, .constantsSummary th{
    background: none repeat scroll 0% 0% #004598 ;
  }
  
  ul.blockList ul.blockList ul.blockList li.blockList h3 {
    background-color: #01030F ;
    border: 1px solid #00589C ;
}
  
  div.details ul.blockList ul.blockList ul.blockList li.blockList h4, div.details ul.blockList ul.blockList ul.blockListLast li.blockList h4 {
    background-color: #111 ;
    border: 1px solid #3399FF ;
}
  
  .overviewSummary, .memberSummary, .typeSummary, .useSummary, .constantsSummary, .deprecatedSummary {
    border-left: 1px solid #004598 ;
    border-right: 1px solid #004598 ;
    border-bottom: 1px solid #004598 ;
}
  
  pre {
    background-color: #131313 ;
    border: 2px solid ;
    box-sizing: border-box ;
    border-radius: 4px ;
    border-color: #0066ff ;
    box-shadow: 1px 1px 7px #3399FF ;
    padding: 10px ;
    margin-left: -8px;
  }
  
  ul > li.tocli {
    background-color: #131313 ;
    border: 1px solid ;
    box-sizing: border-box ;
    border-color: #0066ff ;
    padding: 10px ;
  }
  
  pre b, strong{
    color:#66FF66 ;
  }
  
  em, p > i{
    color: rosybrown ;
  }
  
  tt{
    color:#FF33CC ;
  }
  
  code{
    color:#FFFF66 ;
  }

  .booktitle {
    border-bottom:1px solid #0094FF ;
    background:linear-gradient(to bottom, #002D68 0%, rgba(1, 0, 33, 0.86) 87%, #005DFF 100%) repeat scroll 0% 0% transparent ;
  }
  
  ul.blockList ul.blockList li.blockList, ul.blockList ul.blockListLast li.blockList{
    border: 1px solid #0081FF ;
    background-color: #0C0C0C ;
  }
  
  #TopBar{
    min-width: 0px ;
    border-bottom: 1px solid blue ;
    border-radius: 3px ;
    background: linear-gradient( to bottom, #080808 10%, rgba( 0, 69, 122, 1 ) 90% ) repeat scroll 0% 0% transparent ;
  }
  
  .header-container{
    background-color: #080808 ; /*Very dark gray*/
    border-bottom: 1px solid dodgerblue ;
    box-shadow: none ;
  }
  
  .deprecatedLabel, .descfrmTypeLabel, .memberNameLabel, .memberNameLink, .overrideSpecifyLabel, .packageHierarchyLabel, .paramLabel, .returnLabel, .seeLabel, .simpleTagLabel, .throwsLabel, .typeNameLabel, .typeNameLink {
    color: red ;
  }
  
  #logocover{
    width: 0px ;
  }
  
  .nolinkLESSON, .nolinkAHEAD, .nolinkBHEAD, .nolinkCHEAD, .nolinkQUESTIONS{
    color: red ;
    list-style-type:  initial;
  }
  
  .linkLESSON > a {
    color: blueviolet !important;
  }
  
  .linkAHEAD > a {
    color: deepskyblue !important;
  }
  
  .linkBHEAD > a {
    color: rgba( 60, 255, 0, 1 ) !important;
  }
  
  .linkCHEAD > a {
    color: lightgreen !important;
  }
  
  .linkQUESTIONS > a {
    color: green !important;
  }
  
  #Contents a, .NavBit a, #TagNotes a{
    color: rgba( 60, 255, 0, 1 ) ;
  }
  
  #BreadCrumbs a{
    color: red ;
  }
  
  a:link{
    text-decoration: none ;
    color: rgba( 0, 80, 255, 1 ) ;
  }
  
  #LeftBar {
    border: 1px solid green ;
    border-radius: 4px ;
    padding: 2px ;
  }
  
  #productName{
    color: red ;
  }
}
