css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("weibo.com") {
  /* 广告/烦人东西 */
  .WB_ad_tm2015, .PCD_event_red2015 {display: none}
  .WB_cardwrap[feedtype='ad'] {display: none}
  div[ad-data] {display: none}
  .WB_vipcover {display: none}
  [node-type='feed_spread'] {display: none}
  [node-type='feed_list_shieldKeyword'] {display: none}
  .W_layer_tips_top {display: none}
  /* 聊天 */
  #WB_webim {display: none}
  /* 输入框 */
  #v6_pl_content_publishertop {display: none}
  /* 转发输入框 */
  .froward_wrap .WB_feed_publish {display: none}
  /* 原创/图片…… 标签栏 */
  #v6_pl_content_homefeed .WB_tab_a {display: none}
  /* 傻逼音乐播放器 */
  .PCD_mplayer {display: none}
  /* 导航栏 */
  .WB_global_nav {border-top: 0px !important;
                  background-color: rgba(255, 255, 255, 0.7) !important;}
  /* .gn_position {margin-left:250px} ??? */
  /* 首页、发现、游戏 */
  ul.gn_nav_list > li:not(:last-child) {display:none !important;}
  /* 大家在搜 */
  .WB_global_nav .gn_search_v2 .placeholder {color: transparent !important; display: none;}
  /* 自定义消息背景 */
  .WB_starcover {display: none}
  .WB_feed_detail {background-image: none !important;}
  /* 个人页 补充信息 */
  #wrm_guide_area {display: none}
  /* 回滚 */
  #base_scrollToTop {display: none}
  /* 底部 */
  .WB_footer, .WB_frame_bottom {display: none;}
  /* 新消息 */
  #home_new_feed_tip {margin-top: 0px !important;}
  .WB_cardwrap.WB_notes[requesttype='newFeed'] {margin-top: -7px !important;}
  /* 消息 */
  .WB_feed {margin-top: 10px}
  /* 去掉右栏 */
  .WB_main_r {display: none}
  .WB_main_c {margin-left: 50px; width: 750px !important;}
  /* 左栏颜色 */
  .WB_left_nav .S_txt1, .WB_left_nav .S_ficon {color: #666 !important;}
  .lev_line fieldset {border-color: rgba(0, 0, 0, 0.2) !important;}
  /* 左栏 */
  .WB_left_nav > div:nth-child(1n+5) {display:none !important;}
  /* 中栏 */
  .WB_frame {background-color: #f2f2f2 !important;
             padding-top: 5px !important;
             box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);}
  /* 背景 */
  body {background-color: #f2f2f2 !important;}
  /* 个人页背景 */
  .S_page, .WB_miniblog {background-image: none !important;}
  /* 个人页 banner */
  .pf_use_num, .pf_copy_icon {display: none !important;}
  /* 个人页 请求余额 图片 */
  .PCD_piclist_b {display: none}
  /* 缩略图大小 */
  .WB_media_a {width: 260px !important;}
  .media_box .WB_pic {width: 80px !important; height: 80px !important;}
  /* 导航栏 logo
  .WB_global_nav_us .gn_header, .WB_global_nav .gn_logo .box .logo {
    background: transparent url("http://img.t.sinajs.cn/t6/style/images/global_nav/WB_logo_b.png")
                no-repeat scroll 0% 50% !important;
  }
  .gn_logo {display: none} */
}
