css:
/* Dark Emoticode by Tesla*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.emoticode.net),
               url-prefix(http://emoticode.net){
  body{
    background: none repeat scroll 0 0 #404040 !important;
    margin: 0 0 !important;
    padding: 0 !important;
  }
  body #wrapper{
    margin: 0 auto !important;
  }
  body #footer{
    background-color: #2b2c2e !important;
    bottom: -13em !important;
  }
  div.headblock{
    background: none repeat scroll 0 0 #282828 !important;
  }
  div.create form textarea{
    border: 3px solid #404040 !important;
  }
  div.post p{
    color: #eeeeee !important;
  }
  div.create form textarea{
    border: 3px solid #404040 !important;
  }
  span.follow a{
    background-color: #404040 !important;
    background-image: none !important;
    text-shadow: 0 0 !important;
  }
  li.source{
    background: none repeat scroll 0 0 #282828 !important;
  }
  li.source div.author{
    border-top: 1px solid #404040 !important;
    width: 91% !important;
  }
  li.source h4 a.title{
    color: #d3d3d3 !important;
  }
  li.source div.description{
    color: #9a9a9a !important;
  }
  li.source div.footer{
    background: none repeat scroll 0 0 #4e4e4e !important;
  }
  li.source div.footer ul li{
    border-right: 0 none !important;
    color: #9c9c9c !important;
    text-shadow: 0 0 !important;
  }
  li.source div.footer ul li a{
    color: #9c9c9c !important;
  }
  li.followbox{
    width: 150px;
  }
  li.comment span.content{
    color: #777777 !important;
  }
  a {
    color: #eeeeee !important;
  }
  h1{
    color: #eeeeee !important;
  }
  h2{
    color: #404040 !important;
  }
  h3{
    color: #626262 !important;
  }
  p, pre{
    color: #777777 !important;
  }
  small{
    color: #777777 !important;
  }
  article, aside, details, figcaption, figure, header, hgroup, main, nav, section, summary{
    float: left;
  }
  #ruler{
    background-color: #404040 !important;
    border-bottom: 1px solid #282828 !important;
    border-top: 1px solid #404040 !important;
  }
  #ruler h3.tab a.current{
    background: none repeat scroll 0 0 #282828 !important;
    border-bottom: 1px solid #282828 !important;
    color: #ffffff !important;
  }
  #ruler h3.tab a{
    background: none repeat scroll 0 0 #282828 !important;
    border-left: 1px solid #404040 !important;
    border-right: 1px solid #404040 !important;
    border-top: 1px solid #404040 !important;
    color: #626262 !important;
  }
  #heading{
    border-bottom: 1px solid #404040 !important;
  }
  #sidebar{
    color: #e9e9e9 !important;
  }
  #sidebar ul.events li div.content a{
    color: #eeeeee !important;
  }
  #social{
    background: none repeat scroll 0 0 #404040 !important;
  }
  #social ul li a{
    background-color: #464646 !important;
    border-right: 0 none !important;
  }
  #all_lang span{
    color: #000000 !important;
  }
  #all_langs span{
    color: #ffffff !important;
  }
  #profile_tabs{
    background: none repeat scroll 0 0 #282828 !important;
  }
  #profile #sidebar div.rating{
    color: #ffffff !important;
  }
  #profile_tabs li div.darrow{
    border-top: 15px solid #282828 !important;
  }
  #profile #sidebar div.languages ul li div.bar div.percentage{
    background-color: #777777 !important;
  }
  #profile #sources{
    border-top: 0 !important;
  }
  #blog div.medium-column div.headblock{
    border-radius: 4px;
    margin-left: 23px;
  }
  #source-create form div.head, #source-update form div.head{
    margin-top: 100px !important;
  }
  #epiceditor{
    border: 1px solid #404040 !important;
  }
  #source-create form label, #source-update form label{
    color: #404040 !important;
  }
  #source ul.buttons{
    background-color: #404040 !important;
    background-image: none !important;
    border: 1px solid #282828 !important;
    text-shadow: 0 0 !important;
  }
  #source div.related h3{
    text-shadow: 0 0 !important;
  }
  #source-create form div.head input, #source-create form div.head select, #source-update form div.head input, #source-update form div.head select{
    margin-left: 15px !important;
  }
  #heading form input{
    background-color: #404040 !important;
  }
  #wrapper #settings form label{
    color: #404040 !important;
  }
  #wrapper #settings form textarea, #wrapper #settings form input{
    color: #404040 !important;
  }
  .pagination{
    background-color: #282828 !important;
    box-shadow: 0 0 !important;
  }
  .pagination .current{
    color: #ffffff !important;
    text-shadow: 0 0 !important;
  }
  .pure-form select{
    background-color: #404040 !important;
    border: 1px solid #282828 !important;
  }
  .pure-form input[type="text"], .pure-form input[type="password"], .pure-form input[type="email"], .pure-form input[type="url"], .pure-form input[type="date"], .pure-form input[type="month"], .pure-form input[type="time"], .pure-form input[type="datetime"], .pure-form input[type="datetime-local"], .pure-form input[type="week"], .pure-form input[type="number"], .pure-form input[type="search"], .pure-form input[type="tel"], .pure-form input[type="color"], .pure-form select, .pure-form textarea{
    border: 1px solid #282828 !important;
  }
  .ssummary ul li span.count{
    background: none repeat scroll 0 0 #404040 !important;
    border: 1px solid #404040 !important;
  }
  .ssummary ul li{
    color: #eeeeee !important;
    text-shadow: 0 0 !important;
  }
  .ssummary div.larrow{
    border-right: 15px solid #282828 !important;
  }
}
