 * @stylish-id 106286
 * @author Niklas Gollenstede
 * @licence CC-BY-SA-4.0 or MIT
 * @description
 *     Dark grey/black theme for Stud.IP with red elements.
 *     I only applied a few personal changes and updates for a newer version of Stud.IP to https://userstyles.org/styles/101211/ostfalia-studip-dark, most credit for this theme goes to CaiusCaligulaCC
 *
 *     If you have any issues with this style, please open a ticket at https://github.com/NiklasGollenstede/stylish
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    url-prefix(https://digicampus.uni-augsburg.de/),
    url-prefix(http://studip.ba-emsland.de/),
    url-prefix(https://studip.ba-oldenburg.de/),
    url-prefix(http://studip-bao-leer.de/index.php/),
    url-prefix(https://elearning.uni-bielefeld.de/studip/),
    url-prefix(https://studip.tu-braunschweig.de/),
    url-prefix(https://elearning.uni-bremen.de/),
    url-prefix(https://studip.tu-clausthal.de/),
    url-prefix(http://www.dbs-ip.de/),
    url-prefix(http://studip.ehs-dresden.de/),
    url-prefix(https://elearning.fh-flensburg.de/studip/),
    url-prefix(http://studip.kh-freiburg.de/),
    url-prefix(https://studip.ph-freiburg.de/),
    url-prefix(https://studip.uni-giessen.de/studip/),
    url-prefix(https://studip.hawk-hhg.de/),
    url-prefix(http://studip.uni-greifswald.de/index.php),
    url-prefix(http://h8293.serverkompetenz.net/studip/),
    url-prefix(https://e-learning.tu-harburg.de/studip/),
    url-prefix(https://studip.uni-halle.de/),
    url-prefix(http://www.uni-hannover.de/de/studium/elearning/),
    url-prefix(https://elearning.uni-hannover.de/),
    url-prefix(https://studip.ph-heidelberg.de/),
    url-prefix(https://studip.hawk-hhg.de/),
    url-prefix(http://studip.hs-gm.de/),
    url-prefix(https://studip.hs-harz.de/studip/),
    url-prefix(https://studip.hs-rm.de/),
    url-prefix(https://studip.hawk-hhg.de/),
    url-prefix(https://studip.sw.fh-jena.de/),
    url-prefix(http://lms.ph-karlsruhe.de/studip/),
    url-prefix(http://studip.lsb-niedersachsen.de/),
    url-prefix(http://la.elearningspace.de/),
    url-prefix(https://studienportal.vetmed.uni-muenchen.de/),
    url-prefix(https://studip.nds-sti.de/),
    url-prefix(https://neo.hfwu.de/),
    url-prefix(https://elearning.uni-oldenburg.de/),
    url-prefix(https://elearning.hs-osnabrueck.de/),
    url-prefix(http://studip.serv.uni-osnabrueck.de/),
    url-prefix(https://studip.ostfalia.de/),
    url-prefix(https://studip.uni-passau.de/studip/),
    url-prefix(https://studip.uni-rostock.de/index.php),
    url-prefix(https://studip.fh-schmalkalden.de/),
    url-prefix(https://lms.ph-gmuend.de/),
    url-prefix(https://modulverwaltung.rlp.de/sembbs/),
    url-prefix(https://studip.hochschule-trier.de/),
    url-prefix(https://studip.uni-trier.de/),
    url-prefix(https://studip.app.mba-europe.de/),
    url-prefix(http://www.studip.uni-vechta.de/studip/),
    url-prefix(http://ammerland.elan-ev.de/),
    url-prefix(http://lingen.elan-ev.de/),
    url-prefix(http://meppen.elan-ev.de/),
    url-prefix(http://norden.elan-ev.de/),
    url-prefix(https://studip.hs-wismar.de/),
    url-prefix(http://www.aal-weiterbildung.de/studip/)
{

    /**
     * Most credit goes to:
     * https://userstyles.org/styles/101211/ostfalia-studip-dark
     */

    /**
     * Main theme-colors used:
     * #4C0404 (Darkred)
     * #991818 (Somewhat lighter red)
     * #A6A42B (yellow)
     * #064166 (darkblue)
     * #1996E5 (lighter blue)
     * #181818 (dark grey) for background
     * #282828 (lighter dark grey) for background of blocks
     * Non-greyish colors were created with Adobe Kuler
     */

    /**************************************************
     * Global stuff
     *************************************************/

    /*
     * Various Stuff
     */
    a, a:link, a:visited {
        color: #DDD !important;
    }

    a:hover {
        transition-property: color;
        transition-duration: 0.2s;
        color: #FFB253 !important;
    }

    table.blank, td.blank, td.onlineinfo, td.blanksmall {
        background-color: #282828 !important;
    }

    td.toolbar, td.printhead, td.printhead2, td.printhead3 {
        background-color: #282828 !important;
        background-image: linear-gradient(#282828, #333 40%, #333) !important;
        border-top: 1px solid #991818 !important;
    }

    body {
        color: #DDD;
    }

    body,
    #header,
    #layout_footer {
        background-color: #181818 !important;
        background-image: none;
    }

    #layout_content,
    #layout_sidebar,
    .infobox-img,
    input,
    textarea,
    select,
    td.printcontent {
        background-color: #282828 !important;
        background-image: none;
    }

    .content_body {
        background-color: #383838 !important;
        background-image: none;
    }

    .content_seperator {
        background-color: #484848;
        background-image: none;
        border-color: #A6A42B;
    }

    #context_background {
        background-image: none;
        background-color: #282828;
    }

    a.button, button.button, .button-group button, .button-group .button {
        background-color: #383838;
        background-image: linear-gradient(#585858, #484848 20%, #484848);
        color: #CCC;
        border-color: #064166;
        text-shadow: 0px 0px 0px #FFF;
    }

    textarea, input, select {
        color: white;
    }

    /*
     * Messagebox
     */

    div.messagebox {
        background-color: #A6A42B;
        border-color: #991818;
    }

    /*
     * Navigation Bar with Breadcrumbs, Search...
     */
    #barBottomContainer {
        border-top: 1px solid #BB2222;
        border-bottom: 1px solid #BB2222;
    }

    #barBottomContainer, #barBottomLeft, #barBottommiddle, #barBottomright {
        background-color: #4C0404 !important;
        background-image: none;
    }

    #barBottommiddle:before {
        background-image: linear-gradient(to right, #441818, #4C0404);
        background-color: #4C0404 !important;
    }

    #barBottomright:before {
        background-image: none !important;
    }

    #barBottomright .list {
        background-color: #282828;
    }

    #barBottomright li.notification {
        background-color: #1996E5;
    }

    #barBottomright li.notification:hover {
        background-color: #3DB4FF !important;
    }

    #notification_marker {
        background-color: #064166;
        border: 1px solid #888;
    }

    input.quicksearchbox {
        background-color: #064166;
        color: #DDD;
        border: 1px solid #888;
    }

    /*
     *Tabs
     */
    #tabs li.current, #tabs li.current:hover {
        background-color: #383838;
        background-image: linear-gradient(to bottom, #555, #383838);
        border-bottom-color: #484848;
    }

    #tabs li.current a {
        color: #FFB253 !important;
    }

    #tabs li, #tabs li:hover {
        background-color: #282828;
        background-image: linear-gradient(to bottom, #444, #282828);
        color: #DDD;
        border-color: #484848;
    }

    #tabs2 {
        background-color: #282828;
    }

    /*
     * Tables
     */
    .table_header_bold {
        background-color: #064166 !important;
    }

    th, table.header, .table_header, .table_footer, .table_footer td {
        background-color: #383838 !important;
        color: #DDD !important;
    }

    .table_footer, .table_footer td {
        border-color: #666;
    }

    .blue_gradient {
        background-color: #064166;
        background-image: linear-gradient(#064166, #053654);
    }

    table.zebra > tbody > tr:nth-child(2n+1), table.zebra-hover > tbody > tr:nth-child(2n+1) {
        background-color: #424242;
    }

    table.zebra > tbody > tr, table.zebra-hover > tbody > tr {
        background-color: #383838;
    }

    table.zebra > tbody > tr:nth-child(2n+1):hover, table.zebra-hover > tbody > tr:nth-child(2n+1):hover {
        background-color: #585858;
    }

    table.zebra > tbody > tr:hover, table.zebra-hover > tbody > tr:hover {
        background-color: #525252;
    }

    .table_row_even,
    tr.hover_even,
    table.default:not(.nohover) > tbody:not(.nohover) > tr.hover_even:hover:not(.nohover) > td {
        background-color: #383838;
    }

    .table_row_odd,
    tr.hover_odd,
    table.default:not(.nohover) > tbody:not(.nohover) > tr.hover_odd:hover:not(.nohover) > td {
        background-color: #424242;
    }

    table.default tfoot td,
    tr.sortable th.sortasc, tr.sortable th.sortdesc,
    table.default:not(.nohover) > tbody:not(.nohover) > tr:hover:not(.nohover) > td {
        background-color: #424242;
    }

    table#schedule_data tbody tr:nth-child(2) td,
    table#schedule_data thead tr td {
        background-color: #383838 !important;
    }

    .ui-datepicker-calendar tr td a {
        color: black !important;
    }

    /*
     * Forum-stuff
     */
    #forum_threads {
        border-color: #064166 !important;
    }

    #forum_threads > li:first-child,
    #forum_threads > li > ul > li:first-child {
        border-color: #666 !important;
        background-color: #383838;
    }

    #forum_threads > li,
    #forum_threads > li > ul > li {
        border-color: #666 !important;
        background-color: #383838;
    }

    #forum_threads > li > ul {
        border-color: #666 !important;
        background-color: #383838;
    }

    textarea#new_posting {
        color: #DDD;
    }

    #forum .posting{
        background-color: #111 !important;
    }

    td.areaentry{
        background-color: #111 !important;
        border-bottom: 1px solid #777 !important;
    }

    table#my_seminars>tbody>tr>td:last-child {
        max-width: 30px !important;
        overflow: hidden;
    }

    /**************************************************
     * Index page
     *************************************************/
    #index_navigation {
        color: #EEE;
        background-color: #282828;
    }

    table.index_box td.indexpage {
        display: none;
    }

    #appointments_box .printcontent {
        background-color: #282828 !important;
    }

    table.index_box.logintable {
        color: black;
    }

    /**************************************************
     * Courses page
     *************************************************/
    .infoboxrahmen {
        border-color: #1996E5 !important;
        color: #DDD;
    }

    .infoboxrahmen a {
        color: #A6A42B !important;
    }

    table.infobox td {
        background-color: #064166;
    }

    /**************************************************
     * Messages page
     *************************************************/

    #adressee_2 {
        color: #DDD;
    }

    #subject {
        color: #DDD;
    }

    #message {
        color: #DDD;
    }

    #sms_send .ui-widget-content,
    #sms_send .ui-widget-content .ui-state-focus {
        background-image: none;
        background-color: #383838;
    }

    /**************************************************
     * Community page
     *************************************************/

     /*----------
      * Contacts
      */
     /* Whoever thought that lightgrey was a good idea... it is not! */
     #contact .lightgrey {
        background-color: #484848;
     }

    /* White?! There is no white! There is only black! */
     #contact .white {
        background-color: #484848;
     }

     table.contact-header td {
        background-color: #383838;
     }

     table.contact-header td.active {
        background-color: #424242;
     }

    /**************************************************
     * Community page
     *************************************************/

    .settings thead th {
        background-color: #064166;
    }

    .settings td[colspan]:first-child {
        background-color: #064166;
    }

    /**************************************************
     * Search page
     *************************************************/
     #archiv table, #archiv th, #archiv table.blank, #archiv td.blank {
        background-color: #383838 !important;
     }

    /**************************************************
     * Tools page
     *************************************************/
    #admin_news #layout_table {
        border: none;
        padding: 20px;
        background-color: #282828;
    }

    /**************************************************
     * Blackboard page
     *************************************************/
    .topic {
        background-color: #064166;
    }

    .steel1 {
        background-color: #383838;
    }

    .steelgraulight {
        background-color: #424242;
    }

    .quote {
        background-color: #064166;
        border-color: #1996E5;
    }

    a.extern > img{
        width: 32px !important;
        height: 32px !important;
    }
}
