css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
domain("habrahabr.ru"),
domain("geektimes.ru"){
  #TMpanel,
  #scroll_to_top,
  .promo-block,
  .layout__navbar,
  .page-header,
  .tabs,
  .sidebar_right,
  .column-wrapper_bottom,
  .layout__footer,
  .buttons,
  .html_banner_wrap,
  .similar-posts{
    display: none;
  }
    
  .content_left,
  .layout__base{
    max-width: 1280px !important;
    margin: 0 !important;
    padding: 10px !important;
  }
		
  body,
  .layout,
  .author-info{
    background: #000000 !important;
    color: #D0D0D0 !important;
  }
  
  .post__title_link {
    color: #70A0D0;
  }
  
  .postinfo-panel{
    background: #000000 !important;
    border: none;
  }
  
  h1, h2, h3, h5 {
    color: #60A0FF !important;
  }
  
  h4 {
    color: #C00000 !important;
  }
  
  blockquote {
    margin: 0 !important;
    padding: 0 !important;
  }
  
  .editor .panel {
    background: #202020 !important;
    color: #D0D0D0 !important;
  }
  
  .editor .text-holder textarea,
  .editor .editor__footer{
    background: #000000 !important;
    color: #FFFFFF !important;
    
  }
  
  .message.bad1 {
    background: #200000 !important;
    color: #808080 !important;
  }
  
  .comment-item {
    background: #101010 !important;
    color: #C0C0C0 !important;
  }
  
  .comment-item.is_topic_starter  a{
    color: #FFCC00 !important;
  }
  
  img {
    background: #FFF !important;;
  }
  
  code {
    background: #101010 !important;
    color: #A0FF00 !important;
  }
  
}
