@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("cnbeta.com") {

/*去广告 如广告源不同导致会失败，安装ABP解决 */
ins[id^="aswift"],div[id^="googleAd"],div[id^="tanxssp"],div[id^="BAIDU"],.main_content_right>section:not([class]),.ccenter,.adsbygoogle,#fixed_body,#fixed_area,.bd_r,.bfd_img_logo{display: none !important;}
.article .navi .other_news,.all_news_wildlist .tools .time{display: none !important;}

#rank_box ,#relation_box,h3+.cnbeta-update-list,#BAIDU_DUP_fp_wrapper,.cnbeta-article-wrapper>.w1200:first-child,
ins,iframe,.article-content+.tac,#main-commentbox+.cbv,.pageFooter .media,.pageFooter .sponsors,.pageFooter .about,#COMMENTS ~ .cnbeta-article-latest,
.article-content>.article-relation,.article-content>.article-topic,.article-content>.article-global,.global-navi>.nav-item +.nav-item ,.cnbeta-home-hot-comments.none .cnbeta-side-box,
.topContent>.topContentInner,#left1.cbv,.item>.meta-data+.cnbeta-update-list-article,.meta-data>.tks,
.headline-thumb~ul,.cnbeta-article-vote-tags+.cnbeta-article-latest,.cbv,.cnbeta-headlines-title
{	display: none!important;} /*这部分代码来自云三 */


[style*="important"],[style*="important"]>*{
  height: 0px !important;
  margin: 0px !important;
	font-size: 0px;
	padding: 0 !important;
}	

body {
  padding-top: 0px !important;
}	


/*新版去头部幻灯banner等2017年3月3日 15:18:18*/
.cnbeta-headlines,.cnbeta-main-navi,.cnbeta-home-hero,.cnbeta-today-status,.cnbeta-home-blue-wrapper,.cnbeta-update-list-article {display:none !important;}
/*新版调整列表页样式2017年3月3日*/
.cnbeta-update-list .item dl { padding: 20px 20px 10px 150px;}

.cnbeta-home-side{display:none}
.cnbeta-update-list,.cnbeta-article{width: 890px; float: none; margin: 0 auto;}

.article-share-code{display:none}
}
