css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {

body, a, a:hover {

/*CREDIT TO http://1dcursors.tumblr.com FOR THE CURSOR*/
cursor: url('http://i1279.photobucket.com/albums/y538/1dnersx/Hs_zps24bbb28f.png'), progress !important;

}

body {

/*CREDIT TO http://hipstatease.tumblr.com FOR THE PHOTO*/
  background: 
#040105 url("http://fc04.deviantart.net/fs70/f/2011/150/1/b/kiss_me_or_kill_me_by_tdheather-d3hlj8l.jpg") bottom right fixed no-repeat !important; 
}



.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
  display: none !important; 
}





     
::-webkit-scrollbar-thumb{
background-color: #000;
border: 2px solid #fff;
height:auto;
}
 
::-webkit-scrollbar {
height:9px;
width:9px;
background-color: #000;
border: 4px solid #fff;
}

::selection {
background-color:#f9f9f9;
}



#search_query {
    background-color:  rgba(0,0,0, 0);
    color: rgb(255,255,255);
}


















.left_column {background: none !important;}





.right_column {background: none !important;}



#right_column:after { 
display: none !important; 
}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important; 
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Followers" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Draft" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}





.new_post_label/*_icon*/ > I {
display: none !important
}

.new_post_label { 
   padding-top: 75px !important; 
   color: rgba(255,255,255,0.5) !important; 
   font: italic 19px georgia, serif !important; 
}

.new_post_label:hover { 
   color: rgba(255,255,255,1) !important; 
}

#new_post {

/*CREIDT TO http://halirryween.tumblr.com FOR THE PHOTO*/
    background: #d5d6db url(http://https://lh5.ggpht.com/oPO35ZelIjPJvsRtZqc56LjXWS5kyVVllx-b0G5SDp-CpUgTr534hpw_PdeNrOAQavNpnA=s170) no-repeat left bottom !important;
    height: 100px !important; 
}

#new_post .arrow {
display: none
}
        
        
.l-content {
  background: transparent !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(236,191,201, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(236,191,201, 0.50)  !important;
}




.mceContentBody {
  background: url("http://static.tumblr.com/kuehu0n/FA3mukz0s/harry_styles_tmh.jpg") !important; 
  background-size: 100% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}
#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes,
.selected .activity  {
  color: rgba(255,255,255, 0.9) !important; 
  background: transparent !important; 
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -1px !important; 
}

}
