css:
/* old code start */
body { 
background:url("http://www.hdwallpapers.in/walls/2013_bugatti_veyron_16_4_grand_sport_vitesse-wide.jpg") no-repeat center fixed !important;
	background-size: cover;
}

* {cursor: url("http://fc08.deviantart.net/fs70/f/2012/187/8/7/cursor_2_by_russianblueleona-d568uli.png"), default!important}

::-webkit-scrollbar{width:9px!important;height:9px!important}
::-webkit-scrollbar-button:start:decrement,
::-webkit-scrollbar-button:end:increment{height:0;width:0;display:none}
::-webkit-scrollbar-track-piece{margin:1px;padding:0;width:6px!important;height:5px!important;background:rgba(255,255,255,0.3)}
::-webkit-scrollbar:hover{background:rgba(128,128,128,0.2)}
::-webkit-scrollbar-thumb{margin:1px!important;border:2px solid transparent;width:5px!important;background-color:rgba(0,0,0,0.4)!important;z-index:9999;-webkit-border-radius:12px;background-clip:content-box}
::-webkit-scrollbar-corner{background:rgba(255,255,255,0.3);border:1px solid transparent}::-webkit-scrollbar-thumb:hover{background-color:rgba(0,0,0,0.8)!important;border:2px solid transparent}
::-webkit-scrollbar-thumb:active{background-color:rgba(0,0,0,0.6)!important;border:2px solid transparent}

#jewelCase .jewel{float:left;margin-right: 10px;position:relative}
.jewelCount{
  background-color:#000000;
  top:-1px;
  color:#000000;
  font-size:9px;
  font-weight:bold;
  padding-bottom:1px;
  position:absolute;right:-1px;
  z-index:101;
  -webkit-border-radius:2px
}

.jewelCount span{
	background-color:#000000;
	padding:1px 1px 0;
	-webkit-border-radius:2px
	}

