css:
@-moz-document url(https://tweetdeck.twitter.com/) {
.is-narrow-columns .column {
    width: 265px;
}

a {
    color: #EB7171;
}

a:hover {
    color: #ED5555;
}

.stream-item:hover {
    background: #2B2B2B;
}

.stream-item {
    border-bottom: none;
}

.compose {
    background: #313236;
}

.attach-compose-buttons .tweet-button {
    background: #313236;
}

.is-narrow-columns .column {
    margin-right: 0;
}

.column-header, .column-header-temp {
    background: none;
}

.is-narrow-columns .app-columns {
    padding: 0;
}

.is-inverted-dark .stream-item {
    background: #3e3e3e;
    color: #d7dad1;
}

.fullname {
    color: #d7dad1;
}

.column-temp .column-header, .column-header-temp, .column-head-title {
    border: none;
    color: #d7dad1;
}

.column-temp {
    background: #3e3e3e;
}

.is-inverted-dark .with-column-divider-bottom {
    border: none;
}

.mdl {
    background-color: #292929;
    border-radius: none;
}

.mdl-header-title {
    color: #d7dad1;
}

.mdl-content {
    border: none;
}

.icon-close:before {
    color: red;
}

.inline-reply {
    background: #232426;
}

.is-inverted-dark .tweet-detail-actions, .is-inverted-dark .tweet-stats, .is-inverted-dark .card-holder {
    border: none;
}

.column-background-fill {
    background-color: #232426;
}

.is-inverted-dark .column-scroller::-webkit-scrollbar-track {
    border: none;
}

.btn-on-blue {
    background: #686868;
}

.btn-on-blue:hover {
    background: #575757;
}

.cmp-replyto {
    background: #292929;
    color: #d7dad1;
    border: none;
}

.accs-header {
    color: #d7dad1;
}

.btn-on-blue[disabled], .btn-on-blue[disabled]:hover, .btn-on-blue[disabled]:active, .btn-on-blue.is-disabled, .btn-on-blue.is-disabled:hover, .btn-on-blue.is-disabled:focus, .btn-on-blue.is-disabled:active {
    background: #6e6e6e;
}

.btn-positive, .btn-positive-alt, .btn-fav.s-favorited, .s-following .follow-btn {
    background: #ee5858;
}

.btn-positive:hover {
    background: #EB7171;
}

.btn-positive[disabled], [disabled].btn-positive-alt, [disabled].btn-fav.s-favorited, .s-following [disabled].follow-btn, .btn-positive[disabled]:hover, [disabled].btn-positive-alt:hover, [disabled].btn-fav.s-favorited:hover, .s-following [disabled].follow-btn:hover, .btn-positive[disabled]:active, [disabled].btn-positive-alt:active, [disabled].btn-fav.s-favorited:active, .s-following [disabled].follow-btn:active, .btn-positive.is-disabled, .is-disabled.btn-positive-alt, .is-disabled.btn-fav.s-favorited, .s-following .is-disabled.follow-btn, .btn-positive.is-disabled:hover, .is-disabled.btn-positive-alt:hover, .is-disabled.btn-fav.s-favorited:hover, .s-following .is-disabled.follow-btn:hover, .btn-positive.is-disabled:focus, .is-disabled.btn-positive-alt:focus, .is-disabled.btn-fav.s-favorited:focus, .s-following .is-disabled.follow-btn:focus, .btn-positive.is-disabled:active, .is-disabled.btn-positive-alt:active, .is-disabled.btn-fav.s-favorited:active, .s-following .is-disabled.follow-btn:active {
      color: #d7dad1;
      background: #f17979;
    }
}
