css:
@-moz-document domain("deviantart.com") {
/*moves profile options on header to the left*/
.oh-gap{
    width: 0%;  
}
/* removes trending hashtags */
.featuredtags-widget{
    display: none;
}
/*removes space taken up by trending hashtags*/
.browse-container.newbrowse .browse-top-bar{
    height: 31px;
    margin-top: 1px;
}
#browse-results-page-1.browse-results-page{
  margin-top: -7px;
}
.browse-container.newbrowse .browse-top-bar .search-stats{
  padding-top: 0;
}
/* Changes colour of submit button and unbolds font */
#oh-menu-submit>a>span{
  background-image: linear-gradient(0deg, #58695a, #637566);
  font-weight: normal;
}

}
