css:
@-moz-document url-prefix("http://v.youku.com/v_show/id_") {
  *:not([class*="icon"]):not(i){font-family:"Microsoft Yahei"!important}
  *{text-shadow: silver 0px 0px 1px !important}
  #vpvideoinfov5_wrap,.nBox,.footer-link,.cert,#videocomment,.sideCol,.ad_area{display:none!important}
  .yk-footer-con{padding:20px 0 0 0!important}
  .yk-footer{margin-bottom:0!important}
  .base{padding:10px 20px}
  .page_thx .layout_121 .mainCol{margin:0 auto!important;width:80%!important}
  .layout_121 .mainCol{float:none!important}
  #commentArea{padding-top:20px}
  .page_thx .commentArea .loginBefore textarea{background:#fff!important}
  .yk-interact .fns,.commentArea,.base{border:1px solid #ccc!important;border-radius:5px;box-shadow: 0 0 8px #ccc;padding-left:20px;padding-right:20px;-webkit-transition: all 0.6s ease-in-out;transition: all 0.6s ease-in-out;-moz-transform: all 0.6s ease-in-out}
  .page_thx .base .base_info{width:100%!important;text-align: center!important}
  .yk-interact .fns:hover,.commentArea:hover,.base:hover{box-shadow: 0 0 10px rgb(0,166,231)}
}
