css:
@-moz-document domain("userstyles.org") {
/* 
 * Name: Userstyles.org – Minimalistic stylish look
 * Title: Minimalistic and a stylish look
 * Author:  Crystallis
 * Version: 0.5
 *
 */

a {
color: #2793e6 ! important;
text-decoration: none ! important;
}
a:hover {
color: #2793e6 ! important;
text-decoration: underline ! important;
text-shadow: 0px 0px 3px rgba(39, 147, 230, 1) ! important;
}


html {
background: /*[[hex]]*/ url(/*[[wall]]*/) ! important;
background-size: cover ! important;
background-repeat: no-repeat ! important;
background-position: 50% 50% ! important;
background-attachment: fixed ! important;
}
body {
background: transparent ! important;
color: #808080 ! important;
}
#main-header {
background-color: rgba(0, 0, 0, .9) ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
margin-left: 0px ! important;
}
/*h1 > a {
display: none ! important;
}*/
#main-article {
background: rgba(0, 0, 0, .9) ! important;
-webkit-border-radius: 5px ! important;
-moz-border-radius: 5px ! important;
border-radius: 5px ! important;
padding: 10px ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
}
#left-sidebar {
background: rgba(0, 0, 0, .9) ! important;
-webkit-border-radius: 5px ! important;
-moz-border-radius: 5px ! important;
border-radius: 5px ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
}
#left-sidebar>* {
background-color: transparent ! important;
}
#advanced-search {
border-bottom: 1px solid rgb(40, 40, 40) ! important;
}
.more {
border-top: 1px solid rgb(40, 40, 40) ! important;
}
#screenshots {
border: 1px solid rgb(40, 40, 40) ! important;
}
pre {
border: 1px dashed rgba(40, 40, 40, .63) ! important;
background-color: rgba(18, 18, 18, .44) ! important;
}
section#obsoletion-message {
background-color: rgba(255, 30, 30, .2) ! important;
}
.listing-no-screenshot {
background-color: rgba(80, 80, 80, .2) ! important;
}



.obsolete a, .obsolete a * {
text-decoration: line-through ! important;
}
.obsolete a::after {
content: " - Deleted style" ! important;
}
.install-status a, .install-status a:visited, .install-status a:active {
color: white ! important;
}
.install-status a:hover {
color: #909090 ! important;
}

#discussions .good-rating {
list-style-image: url(https://i.imgur.com/xc5UhTc.jpg) ! important;
}
#discussions .bad-rating {
list-style-image: url(https://i.imgur.com/gaDCICt.jpg) ! important;
}

img.good-average-rating {
content: url(http://i.imgur.com/xc5UhTc.jpg) ! important;
}
img.bad-average-rating {
content: url(https://i.imgur.com/gaDCICt.jpg) ! important;
}



/* FORUM */

#Head {
background: rgba(0, 0, 0, .9) ! important;
-webkit-border-radius: 5px ! important;
-moz-border-radius: 5px ! important;
border-radius: 5px ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
color: #fff;
}
#Body {
padding-top: 15px ! important;
}
#Panel {
background: rgba(0, 0, 0, .9) ! important;
-webkit-border-radius: 5px ! important;
-moz-border-radius: 5px ! important;
border-radius: 5px ! important;
padding: 10px ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
}
#Body .ContentColumn {
background: rgba(0, 0, 0, .9) ! important;
-webkit-border-radius: 5px ! important;
-moz-border-radius: 5px ! important;
border-radius: 5px ! important;
padding: 10px ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
}
.Attachment:hover .FileHover {
background: rgba(0, 0, 0, .95) ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
}
.Thumb-Extension {
color: #707070 ! important;
}
.PanelCategories li:first-child, .PanelCategories li:nth-last-child(2) {
border-bottom: 1px solid rgba(255, 255, 255, .075) ! important;
}
.Item.Read {
background: rgba(30, 30, 30, .8) ! important;
opacity: none ! important;
}
.Item.Read:hover {
background: rgba(30, 30, 30, .9) ! important;
opacity: none ! important;
}
.OptionsTitle {
background: url("https://i.imgur.com/v8u93lT.jpg") no-repeat 0 -101px ! important;
}
.MenuItems {
line-height: 100%;
font-size: 11px;
border: 1px solid rgb(0, 0, 0) ! important;
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
background: rgba(0, 0, 0, .85) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
}
.Flyout:before {
border-bottom: 7px solid rgb(0, 0, 0) ! important;
}
.Flyout:after {
border-bottom: 7px solid rgba(0, 0, 0, .9) ! important;
}
.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {
color: #707070 ! important;
}
.MenuItems a:hover {
color: #808080 ! important;
text-shadow: none ! important;
text-decoration: none ! important;
background-color: rgba(255, 255, 255, .095) ! important;
}

table.PreferenceGroup tbody tr:hover td {
background: rgba(255, 255, 255, .075) ! important;
}
.MeButton:hover {
background: rgba(255, 255, 255, .075) ! important;
}
.MenuItems hr {
border: 0;
border-bottom: 1px solid rgba(255, 255, 255, .075) ! important;
}
.FlyoutMenu {
background: rgba(0, 0, 0, .9) ! important;
color: #707070 ! important;
border-radius: 5px ! important;
-moz-border-radius: 5px ! important;
-webkit-border-radius: 5px ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
border: solid 1px rgb(0, 0, 0) ! important;
}
.PopList .Item:hover {
background: rgba(255, 255, 255, .075) ! important;
}

/* FORUM */







::-webkit-scrollbar {
width: 10px ! important;
height: 10px ! important;
background: rgb(30, 30, 30) ! important;
border: 1px solid rgb(37, 37, 37) ! important;
}
::-webkit-scrollbar-button {
display: none ! important;
}
::-webkit-scrollbar-thumb,
::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}
::-webkit-scrollbar-thumb {
min-height: 28px ! important;
background: rgb(78, 78, 78) ! important;
}
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track {
background: rgb(30, 30, 30) ! important;
}



textarea {
background-color: rgba(20, 20, 20, .8) ! important;
border: 1px solid rgba(40, 40, 40, .8) ! important;
border-top: 1px solid rgba(42, 42, 42, .9) ! important;
color: #707070 ! important;
}
input[id*="style_short_description"],
input[id*="example-url"],
input[name*="new_screenshot_description_"],
input[name*="screenshot_description_"],
input[id*="style_pledgie_id"],
input[id*="search-terms"],
input[id*="user_login"],
input[id*="password"],
input[id*="password_confirmation"],
input[id*="user_name"],
input[id*="user_email"],
input[id*="user_homepage"],
input[type*="url"],
input[id*="user_paypal_email"],
input[id*="widget-title"],
input[id*="obsoleting-style-id"] {
background-color: rgba(20, 20, 20, .8) ! important;
border: 1px solid rgba(40, 40, 40, .8) ! important;
border-top: 1px solid rgba(42, 42, 42, .9) ! important;
color: #707070 ! important;
}
input[id*="setting-"] {
border: 1px solid rgba(40, 40, 40, .8) ! important;
border-top: 1px solid rgba(42, 42, 42, .9) ! important;
color: #707070 ! important;
}
select {
border: 1px solid rgba(40, 40, 40, .8) ! important;
color: #707070 ! important;
background-color: rgba(20, 20, 20, .8) ! important;
}





footer > p {
background: rgba(0, 0, 0, .9) ! important;
-webkit-border-radius: 5px ! important;
-moz-border-radius: 5px ! important;
border-radius: 5px ! important;
padding: 10px ! important;
border: 1px solid rgb(0, 0, 0) ! important;
-webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
-moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, .5) ! important;
position: fixed ! important;
bottom: 0px ! important;
right: 10px ! important;
}


}
