/*
Change this to your router IP if different.
*/
@-moz-document domain("192.168.1.1") {

  body {
    background: url('http://subtlepatterns.com/patterns/footer_lodyas.png') repeat scroll 0% 0% #333333;
  }
  #header {
    background: #53BF6B;
  }
  
  #navi a {
    color: #53BF6B;
  }
  
  #navi a:hover {
    background: #53BF6B;
  }
  
  #navi a.indent1 {
    font-weight: bolder;
    font-size: 1.1em;
  }
  
  #navi a.active {
    color: white;
    background: #53BF6B;
  }
  
  .section-title {
    font-weight: bolder;
    font-size: 1.5em;
  }
  
  .section a {
    color: #53BF6B;
  }
  
  .section a:hover {
    background: #53BF6B;
    color: white;
  }
  
  td#content a {
    color: #53BF6B;
  }
  
  td#content a:hover {
    background: #53BF6B;
    color: white;
  }
  
  input[type="button"] {
    background: #53BF6B;
    color: white;
    padding: 5px;
    border: none;
  }
  
  input[type="button"]:hover {
    background: #4b945b;
    cursor: pointer;
  }
  .tomato-grid {
    border: none;
  }
  .tomato-grid .header {
    background: #53BF6B;
    color: white;
  }
  
  tr.footer .co1,tr.footer .co2,tr.footer .co3,tr.footer .co4,tr.footer .co5 {
    font-style: italic;
    color: grey;
  }
  
  tr.even .co4, tr.odd .co4 {
    color: #4E92F2;
  }
  tr.even .co3, tr.odd .co3 {
    color: #4FB877;
  }
  
  tr.even .co5, tr.odd .co5 {
    color: #F24E92;
  }
  
  ul.tabs a.active, #tabs a.active {
    background: #53BF6B;
  }
}
