@-moz-document domain("vbox7.com") {
    body {
        background: url('http://picbg.net/u/7282/57100/735864.png') repeat;
    }
    
    * {
        outline: 0 !important;
    }

       .col-right, .home-avt-rbox-holder, .editors-choice, .home-row-lbox:first-child, .home-row-lbox:nth-child(3), .home-row-lbox:nth-child(4), .home-row-bottom, #footer, #header-top, #header-bottom, #headerLine, .inner-right {
