     #header-main {
         margin-top: 30px;
        box-shadow: 2px 2px 16px rgba(0,0,0,0.4);
     }
    a {
        color: #666 !important;
    }
    
    #def-sort-control-btn:hover, .avatar-menu-btn:hover, .def-sort-control:hover, .defaultBtn, .unsubscribe-btn, #play-save-button:hover {
        background: #999 !important;
