    #inpsearch:hover, .search-btn:hover, .search-select:hover, #inpsearch:hover {
        background: #fafafa !important;
        border: 0 !important;
    }
    
    .search-btn { 
       background: #fafafa !important;
    }
    
    #mainContainer {
        padding-top: 50px;
    }
    
    .inner-list-wrapper-15 {
        box-shadow: 2px 2px 16px rgba(0,0,0,0.4);
    }
    
    .avatar-menu li a:hover {
        background: #ccc !important;
    }
    
    .tab > a, .tab  {
        background: transparent !important;
       
    }
    
    .def-sort-menu {
       width:99%;
