    }
    
    h3 {
        color: #777 !important;
    }
    
    .avatar-menu-btn{
        height: 34px !important;
    }
    
    #playPage .player{
        margin-left: 80px !important;
    }
    
    #play-video-title {
        margin-left: 80px !important;
        color: #999 !important;
        text-shadow: 1px 0 1px #aaa;
    }
    
    #play-video-flag{
        margin-right: 50px !important;
    }
    
    .subscribe-btn{ 
     background: #999 !important;
     color: #eee !important;
        border: none !important;
        margin-top: 3px !important;
    }
    
    .subscribed-label {
         margin-top: 14px !important;
    }
    
    #play-vote-up:hover, #play-vote-down:hover {
        background: #999 !important;
        border: 0!important;
    }
    
    .title-pink {
        color: #999 !important;
        text-shadow: 1px 0 1px #aaa;
        margin-left: -60px;
    }
    
    .tabs-common > .active {
        border-bottom: 1px solid #ccc !important;
    }
    
    li a:hover {
        background: none !important;
        border-right: 1px solid #ccc !important;
        color: #333 !important;
    }
    
    .sort-bar {
        margin-bottom: 10px !important;
    }
    
    .paginationNew a:hover{
        border: 1px solid #999 !important;
        color: #999 !important;
        
    }
    
    .prevPage, .nextPage {
        width: 30px;
    }
    
    .prevPage a:before {
        content: "<";
        color: #999 !important;
        margin-left: -2px;
    }
    
    .nextPage a:before {
        content: ">";
        color: #999 !important;
        margin-left: -1px;
    }
    
    .prevPage a:hover:before, .nextPage a:hover:before {
        color: #666 !important;
    }
    
    #search:hover {
        background: #fafafa !important; 
    }
    
    .search-select:hover {
        background: none !important;       
    }
    
    .search-opt-menu ul li a:hover {
        color: #333 !important;
    }
     
    .inner-left  {
        width: 100% !important;
    }

