css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("piazza.com") {
  #popular_tags_bar {
    height: 29px !important;
    padding-top: 3px !important;
  }
  #popular_tags_bar .tag {
    font-family: Tahoma, Geneva, sans-serif !important;
    margin-top: 6px !important;
    padding-top: 0px !important;
  }
  #popular_tags_bar .popular_tag_number {
    font-size: 10px !important;
    margin-top: -2px !important;
  }
  #page_main.has_popular_tags_bar > .views{
    top: 30px !important;
  }
  #page_main .main_panel {
    background-color: #DBDBDB !important;
  }
  .tag {
    text-transform: uppercase !important;
    font-size: 12px !important;
  }
  
  /* Post Content */
  
  .slider.slider-horizontal{
    width: 100% !important;
  }
  .slider-red {
    background-color: #3E7AAB !important;
  }
  .post_region_box {
    border-radius: 0px !important;
    /* border-bottom: 1px solid gray !important;*/
    box-shadow: 4px !important;
    margin: 1px 0 8px 0 !important;
  }
  .post_region_box .post_region_header {
    padding: 8px !important;
  }
  .post_region_box .post_region_actions {
    font-size: 12px !important;
    line-height: 21px !important;
  }
  .post_region_box .post_region_actions .post_region_actions_meta {
    font-size: 12px !important;
    line-height: 25px !important;
  }
  .post_region_box .post_region_header .follow_link {
    font-size: 12px !important;
    line-height: 22px !important;
    color: white !important;
  }
  .post_region_box .post_region_arrow_dropdown {
    right: 7px !important;
    top: 39px !important;
  }
  .post_region_box .post_region_header .post_title {
    color: white !important;
    /*text-transform: uppercase !important;
    font-weight: normal;*/
  }
  .post_region_box .post_region_header .post_subtitle {
    color: white !important;
    /*text-transform: uppercase !important;
    font-weight: normal;*/
  }
  .post_region_box .post_region_header .post_icon{
    display: none !important;
  }
  #question .post_region_header {
background: rgb(209,0,0); /* Old browsers */
background: -moz-linear-gradient(top, rgba(209,0,0,1) 0%, rgba(135,18,18,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(209,0,0,1)), color-stop(100%,rgba(135,18,18,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(209,0,0,1) 0%,rgba(135,18,18,1) 100%); /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(209,0,0,1) 0%,rgba(135,18,18,1) 100%); /* W3C */
  }
  #s_answer .post_region_header {
background: rgb(25,170,42); /* Old browsers */
background: -moz-linear-gradient(top, rgba(25,170,42,1) 0%, rgba(1,114,0,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(25,170,42,1)), color-stop(100%,rgba(1,114,0,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(25,170,42,1) 0%,rgba(1,114,0,1) 100%); /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(25,170,42,1) 0%,rgba(1,114,0,1) 100%); /* W3C */
  }
  #i_answer .post_region_header {
background: rgb(255,93,0); /* Old browsers */
background: -moz-linear-gradient(top, rgba(255,93,0,1) 0%, rgba(165,77,9,1) 99%, rgba(239,108,14,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255,93,0,1)), color-stop(99%,rgba(165,77,9,1)), color-stop(100%,rgba(239,108,14,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(255,93,0,1) 0%,rgba(165,77,9,1) 99%,rgba(239,108,14,1) 100%); /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(255,93,0,1) 0%,rgba(165,77,9,1) 99%,rgba(239,108,14,1) 100%); /* W3C */
  }
  #clarifying_discussion .post_region_header {
background: rgb(24,109,226); /* Old browsers */
background: -moz-linear-gradient(top, rgba(24,109,226,1) 0%, rgba(12,50,155,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(24,109,226,1)), color-stop(100%,rgba(12,50,155,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(24,109,226,1) 0%,rgba(12,50,155,1) 100%); /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(24,109,226,1) 0%,rgba(12,50,155,1) 100%); /* W3C */
  }
  #note .post_region_header {
background: rgb(137,137,137); /* Old browsers */
background: -moz-linear-gradient(top, rgba(137,137,137,1) 0%, rgba(56,56,56,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(137,137,137,1)), color-stop(100%,rgba(56,56,56,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(137,137,137,1) 0%,rgba(56,56,56,1) 100%); /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(137,137,137,1) 0%,rgba(56,56,56,1) 100%); /* W3C */
  }
  #poll .post_region_header {
background: rgb(137,137,137); /* Old browsers */
background: -moz-linear-gradient(top, rgba(137,137,137,1) 0%, rgba(56,56,56,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(137,137,137,1)), color-stop(100%,rgba(56,56,56,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(137,137,137,1) 0%,rgba(56,56,56,1) 100%); /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(137,137,137,1) 0%,rgba(56,56,56,1) 100%); /* W3C */
  }
  .post_region_box .post_region_message_wrapper .post_region_message.show {
    margin-top: 12px !important;
    max-width: 400px !important;
    left: 0 !important;
    right: 0 !important;
    margin-left: auto;
    margin-right: auto;
  }
  .post_region_box .post_region_message_wrapper {
    margin-top: 0px !important;
  }
  .post_region_box .post_region_message_wrapper .post_region_message.endorse {
    position: absolute !important;
    left: 0 !important;
    right: 3% !important;
    margin-top: 23px !important;
  }
  .post_region_content .clarifying_discussion {
    margin: 0 -12px 0px !important;
    padding: 8px 12px !important;
    background-color: #ececec !important;
  }
  .post_region_box .discussion_replies {
    background: white !important;
    border-bottom-right-radius: 0px !important;
    border-bottom-left-radius: 7px !important;
    margin-top: 2px !important;
    padding: 6px 6px 0px !important;
    border-top: solid #478ff2 1px !important;
    /*box-shadow: 0px 3px 1px #888888;*/
    /*border: 1px solid gray;*/
  }
  .post_region_box .resolve_unresolve_actions{
    background: #40783c !important;
  }
  .post_region_box .unresolved .resolve_unresolve_actions{
    background: #991B1E !important;
  }
  .actual_reply_text.post_region_text{
    display: block !important;
    margin-top: 0px !important;
  }
  #start_new_followup_header {
    margin-top: 6px !important;
  }
  
  /* Question Feed */
  .page_feed .page_feed_search_wrapper .search_bar{
    box-shadow: 0px 0px 3px #CCC, 0px 1px 2px #BBB inset;
  }
  #question_feed_questions.scrollable {
    /*overflow: hidden !important;*/
    margin-top: 2px !important;
  }
  /*
  #question_feed_questions.scrollable:hover {
    overflow-y: visible !important;
  }*/
  .start_open.page_feed_bucket_header, .page_feed .page_feed_bucket_header {
background: rgb(114,114,114) !important; /* Old browsers */
background: -moz-linear-gradient(top, rgba(114,114,114,1) 0%, rgba(68,68,68,1) 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(114,114,114,1)), color-stop(100%,rgba(68,68,68,1))) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(114,114,114,1) 0%,rgba(68,68,68,1) 100%) !important; /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(114,114,114,1) 0%,rgba(68,68,68,1) 100%) !important; /* W3C */
  }
  .page_feed .feed_item.unread .unread_indicator {
background: rgb(109,179,242) !important; /* Old browsers */
background: -moz-linear-gradient(top, rgba(109,179,242,1) 0%, rgba(84,163,238,1) 50%, rgba(54,144,240,1) 51%, rgba(30,105,222,1) 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(109,179,242,1)), color-stop(50%,rgba(84,163,238,1)), color-stop(51%,rgba(54,144,240,1)), color-stop(100%,rgba(30,105,222,1))) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(109,179,242,1) 0%,rgba(84,163,238,1) 50%,rgba(54,144,240,1) 51%,rgba(30,105,222,1) 100%) !important; /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, rgba(109,179,242,1) 0%,rgba(84,163,238,1) 50%,rgba(54,144,240,1) 51%,rgba(30,105,222,1) 100%) !important; /* W3C */
  }
  .page_feed .page_feed_search_wrapper {
    height: 40px !important;
  }
  .page_feed .feed_item .unread_indicator {
    width: 12px !important;
  }
  .page_feed .feed_item.selected {
  /* TODO Fix for this and unanswered */
  }
  #question_feed_questions .question_group {
    margin-top: -1px !important;
  }
  .page_feed .page_feed_bucket_header {
    color: white !important;
    text-shadow: none !important;
  }
}
