@-moz-document domain("gamefront.com") {
/* Image Settings */

.files__box,
.game__cardHome,
.latest-threads__item,
.latest-threads__item,
.matchheight .box,
.site__col-articles,
.site__col.slick-slide,
.slick-slide,
.video__home,

[style="padding-right: 3px; padding-bottom: 3px; display: inline-block;"]
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.files__box:hover,
.game__cardHome:hover,
.latest-threads__item:hover,
.latest-threads__item:hover,
.matchheight .box:hover,
.site__col-articles:hover,
.site__col.slick-slide:hover,
.slick-slide:hover,
.video__home:hover,

[style="padding-right: 3px; padding-bottom: 3px; display: inline-block;"]:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

.card__image,
img {
	border-radius: 22px!important;
}

.article-card__heading,
.profile__name {
	border-bottom-left-radius: 22px!important;
	border-bottom-right-radius: 22px!important;
}

/*__________________________________________________________________________________*/

.card__image,
img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.site__col-articles:hover p,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

}

