/* lolibook.dark v1.1 by Franc[e]sco / lolisamurai */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(http://)?lolis\\.org/(index\\.php\\?do=/(?!profile-.*).*)?") {
  body, h1, h2, h3 {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    color: #f6f3e8;
  }
  
  body {
    font-size: 11px;
  }
  
  #header_menu_page_holder {
    font-size: 11px;
  }
  
  a:link, 
  a:visited, 
  a:active, 
  div#welcome .user_display_name a:link, 
  div#welcome .user_display_name a:visited, 
  div#welcome .user_display_name a:active, 
  #content_load_data h1 a:link,
  #content_load_data h1 a:visited, 
  #content_load_data h1 a:active, 
  #content_load_data div.row_title_info h1 a.link, 
  .home-blog-item .content a:link {
    color: #cae682;
  }
  
  a:hover, 
  div#welcome .user_display_name a:hover, 
  #content_load_data h1 a:hover {
    color: #e5786d; 
  }
  
  .table_left,  
  .js_box_content, 
  #privacy_holder_table .table_left {
    color: #f6f3e8; 
  }
  
  .js_box_close a:link, 
  a.page_section_menu_link, 
  .item_is_not_active, 
  .item_is_active {
    color: #333; 
  }
  
  .block .block_headline {
    color: #f6f3e8; 
    border-color: #3c4043;
  }
  
  ul.activity_feed_form_attach li a.active {
    color: #e5786d; 
  }
  
  #left .block .title, 
  #right .block .title, 
  #breadcrumb_list ul li a, 
  #privacy_holder_table .extra_info, 
  #privacy_holder_table .table {
    background-color: #3c4043;
    border-color: #3c4043;
    color: #f6f3e8; 
  }
  
  #main_content_holder, 
  a#pages_like_join {
    background-image: none;
    background-color: #242424;
  }

  div.header_bar_menu ul.header_bar_drop li a {
    border: 0; 
    color: #f6f3e8; 
  }
  
  .sub_section_menu ul li a, 
  ul.action li a {
    color: #f6f3e8; 
  }
  
  .activity_feed_form {
    background-color: #3c4043;
    border-color: #3c4043;
  }

  div.comment_mini_content_border {
    background-color: #3c4043;
    border-color: #3c4043;
  }

  .activity_feed_form_button {
    background-color: #3c4043;
    border-color: #3c4043;
    background-image: none;
    border: none;
  }

  div.header_bar_menu {
    background-image: none;
    background-color: #3c4043;
    border-color: #3c4043;
  }
  
  .feed_share_holder, 
  .pager_view_more_link a, 
  .sub_menu_bar a, 
  .js_box_content {
    background-color: #3c4043;
    border-color: #3c4043;
  }

  .feed_sort_order a.feed_sort_order_link, 
  .sub_section_menu ul li.menu_line, 
  .user_browse_content, 
  a.pages_type_add_inner_link, 
  .forum_last_post, 
  .forum_thread_last_post, 
  .votes, 
  .user_register_holder, 
  #member-features .perks {
    background-color: #3c4043;
  }
  
  div.comment_mini_end {
    background-image: none;
    border: none;
  }

  #js_is_user_profile .profile_header, 
  a.activity_feed_content_view_more, 
  div.row2, 
  div.row1, 
  .photo_holder, 
  div#welcome, 
  .feed_sort_order, 
  .label_flow, 
  #breadcrumb_holder, 
  ul.action li a, 
  .parent_item_feed .js_feed_comment_border, 
  .profile_no_timeline a#pages_like_join, 
  .forum_last_post, 
  .table_row, 
  .forum_thread_last_post, 
  .forum_thread_view_holder, 
  .item_tag_holder, 
  .votes, 
  .video_image_border {
    border-color: #3c4043;
  }
  
  .comment_mini_child_content div.comment_mini {
    border-color: #52585c
  }

  div#left.ui-sortable, 
  #left, 
  div.comment_mini, 
  div.header_bar_menu ul.header_bar_drop li span, 
  a#pages_like_join {
    border: 0;
  }
}
