css:
@-moz-document domain("greasyfork.org"), domain("www.greasyfork.org") {
/* Common Fonts */
body, input {
  font-family: "/*[[normal-font]]*/", 'Microsoft YaHei UI', sans-serif;
}
pre, code {
  font-family: "/*[[mono-font]]*/", NSimSum, monospace;
}

/* Additional Info */
#additional-info > div h1,
#additional-info > div h2 {
  line-height: 1.2;
  border-bottom: 1px solid #ccc;
  font-size: 2.25em;
  margin-bottom: 0.3em;
}

#additional-info > div h2 {
  font-size: 1.75em;
}
#additional-info > div code {
  padding: 0.2em 0.5em;
  margin: 0;
  font-size: 85%;
  background: rgba(200,200,200,.4);
  border-radius: 3px;
}

#additional-info > div blockquote {
  color: #444;
  border-left: 4px solid #ddd;
  margin-left: 0;
  padding: 0.2em 0;
  padding-left: 2em;
  background: #eee;
}


#additional-info > div pre {
  word-wrap: normal;
  padding: 16px;
  overflow: auto;
  font-size: 85%;
  line-height: 1.45;
  background-color: #f7f7f7;
  border-radius: 3px;
}

/* Forum */

.Item-Body > .Message pre a,
.Item-Body > .Message code a {
  pointer-events: none;
  cursor: default;
  color: inherit;
}

.Item-Body > .Message {
  font-size: 0.85rem;
}

#Form_Rating1 ~ img {
  margin-right: 5em;
}

#Form_Rating1::after {
  margin-left: 40px;
  width: 5em;
  content: '举报脚本';
  display: inline-block;
}
}
