css:
@-moz-document domain(allesausseraas.wordpress.com) {
  div.avatar-wrapper, span.says, .comment-likes, .recentcommentsavatartop, .recentcommentsavatarend  {
    display: none !important;
  }
  .comments-area {
    font-family: Helvetica,Arial,sans-serif;
  }
  .comment-content {
    margin-top: 10px !important;
    font-size: 13px !important;
    line-height: 1.5em !important;
  }
  .comment-content p {
    margin-bottom: 0px !important;
  }
  .comment-metadata {
    font-size: 12px !important;
  }
  .comment-meta {
    background-color: #EEEDE5;
    padding-left: 10px;
  }
  cite.fn {
    font-size: 16px !important;
    color: black !important;
    font-weight: bold !important;
    font-style: normal !important;
  }
  .comment-author a.url {
    text-decoration: underline;
  }
  /* .bypostauthor .comment-meta {
    background-color: gold;
  } */
  .widget_recent_comments {
    position: fixed !important;
    left: 5px !important;
    top: 50px !important;
  }
  #recent-comments-2 > .recentcommentsavatar {
    display : none; 
  }
  #recent-comments-2:hover .recentcommentsavatar {
    background-color: AliceBlue;
    margin: 5px;
    display: block;
  }
}
