css:
@-moz-document domain("facebook.com"){

  /****************************
    Chat window
  ****************************/

  /* Open chat windows */
  .fbNubGroup .fbNub.opened { width:280px !important; }
  .fbNubGroup .fbDockChatTabFlyout { height:auto !important; }
  .fbDockChatTabFlyout .fbNubFlyoutBody { height:350px !important; }

  /* Conversation padding */
  .fbNubGroup .conversation { padding:0 2px !important; }

  /* Chat Header */
  .fbNubFlyoutTitlebar { z-index: 2 !important; }
  .fbNubFlyoutHeader {
    position: absolute !important;
    z-index: 1;
    top: 0px !important;
    left: 0px !important;
    right: 0px !important;
    height: 0 !important;
    overflow: hidden !important;
    opacity: 0 !important;
    border-bottom: 1px solid rgba(0,0,0,0.15) !important;
    -webkit-transition: 250ms;
    -moz-transition: 250ms;
    -ms-transition: 250ms;
    -o-transition: 250ms;
    transition: 250ms;
  }
  .fbNubFlyoutHeader:hover, .fbNubFlyoutTitlebar:hover + .fbNubFlyoutHeader {
    top: 28px !important;
    height: auto !important;
    opacity: 1 !important;
  }

  /* Chat bubble typography */
  .fbNubFlyoutBody ._5w1r {
    padding: 3px 3px !important;
    font-size: 13px !important;
    -webkit-text-shadow: none;
    -moz-text-shadow: none;
    -o-text-shadow: none;
    text-shadow: none;
  }

  /* Slightly increase comment text size for easier reading */
  .UFIComment { font-size:1.05em; }

}
