css:
@-moz-document url-prefix("http://gmtool.wf.pvt"), url-prefix("http://pw.gmtool.pvt"), url-prefix("http://gmtool.dn.pvt"), url-prefix("http://gmtool.aa.pvt"), url-prefix("http://gmtool.aw.pvt"), url-prefix("http://gmtool.pw.pvt/"), url-prefix("http://gmtool.ddt.pvt"), url-prefix("http://jd.gmtool.pvt"), url-prefix("http://gmtool.jd.pvt") {
html,
tbody,
div.section {
    color: #F8FEDD;
    background-color: #1E1E1E;
}
/*работа с аккаунтами*/

FORM.vertical_form {
    border: 1px solid #434346;
}
/*заголовок*/

.ui-widget-header {
    border: 1px solid #434346;
    background: #F6A828;
    color: #1E1E1E;
}
td.border_left,
td.border_right {
    border-right: 0px solid #F7F7F7;
    border-left: 0px solid #F7F7F7;
}
.border_left {
    border-left: 0px solid #F7F7F7;
}
/*ввод текста*/

INPUT,
SELECT,
TEXTAREA {
    color: #F8FEDD;
    background-color: #1E1E1E;
}
INPUT:focus,
SELECT:focus,
TEXTAREA:focus {
    color: #F8FEDD;
    background-color: #434346;
}
/*списоки, таблицы*/

.list {
    background-color: #1E1E1E!important;
    border: 1px solid #434346!important;
}
.row_odd {
    color: #F8FEDD!important;
    background-color: #434346!important;
}
.row_even {
    color: #F8FEDD!important;
    background-color: #1E1E1E!important;
}
.row_odd:hover {
    color: #F8FEDD!important;
    background-color: #000!important;
}
.row_even:hover {
    color: #F8FEDD!important;
    background-color: #000!important;
}
.headerSortUp {
    color: #F8FEDD!important;
    background-color: #434346!important;
}
/*юзеры мыла*/

.mailruuser {
    color: #F8FEDD;
    background-color: #513928 !important;
}
/*главное меню*/

.ddsmoothmenu-v ul li {
    border: #434346;
}
#mainmenu > ul > li > a,
#mainmenu > ul > li > span,
.ddsmoothmenu ul li {
    color: #00BFFF;
    background-color: #1E1E1E;
    background-image: none!important;
    border: 1px solid #434346;
}
#mainmenu > ul > li > a:hover,
#mainmenu > ul > li > span:hover {
    color: #00BFFF;
    background-color: #434346;
    background-image: none!important;
    border: 1px solid #434346;
}
#mainmenu ul li ul li a:link,
#mainmenu ul li ul li a:visited,
#mainmenu ul li ul li a:active {
    color: #00BFFF;
    background-color: #1E1E1E;
    background-image: none!important;
}
#mainmenu ul ul a:hover {
    color: #00BFFF;
    background-color: #434346!important;
    background-image: none!important;
}
.active {
    border: 2px solid #434346!important;
    border-radius: 4px;
    background-color: #1E1E1E !important;
}
/*действия с акком*/

#accountsmenu {
    background: none repeat scroll 0% 0% #F8FEDD;
    background-color: #1E1E1E;
    color: #00BFFF!important;
}
.ddsmoothmenu ul li a,
.ddsmoothmenu ul li span {
    color: #00BFFF!important;
}
#accountsmenu:hover,
.ddsmoothmenu ul li:hover {
    background-color: #434346;
    color: #00BFFF!important;
}
.ddsmoothmenu ul li a:hover,
.ddsmoothmenu ul li span:hover {
    color: #00BFFF!important;
    background-color: #434346!important;
}
.ddsmoothmenu ul li:hover,
.ddsmoothmenu ul li:hover {
    background-color: #434346!important;
}
/*сортировка*/

.sorter {
    color: #F8FEDD;
}
/*ссылки*/

a:link {
    color: #00BFFF;
}
a:visited {
    color: #569CD6;
}
.ban_button {
    background: none repeat scroll 0% 0% #000;
    border: 1px solid #434346;
}
.mailruuser:hover {
    background-color: #000 !important;
}
/*коммент и лог*/

.suitup-editor {
    border-right: 1px solid #1E1E1E;
    border-color: #1E1E1E;
    background-color: #434346;
}
.suitup-controls {
    border-right: 1px solid #1E1E1E;
    border-color: #1E1E1E;
    background: linear-gradient(to bottom, #F8FEDD 0%, #1E1E1E 100%) repeat scroll 0% 0% transparent;
}
/*календарь*/

[class="ui-state-default"] {
    border: 1px solid #434346!important;
    background: repeat-x scroll 50% 50% #222222 !important;
    color: #F6A828;
}
#ui-datepicker-div,
.ui-widget-content,
#ui-timepicker-div {
    background-color: #1E1E1E !important;
    border: 1px solid #434346;
    background: repeat-x scroll 50% 50% #222222 !important;
    background-image: none;
}
.ui-state-active,
.ui-widget-content .ui-state-active,
#ui-datepicker-div {
    border: 1px solid #434346;
    background: repeat-x scroll 50% 50% #1E1E1E !important;
    color: #F6A828;
}
#ui-timepicker-div {
    border-bottom: 1px solid #434346 !important;
}
table.tablesorter tr th {
    background-color: #1E1E1E;
    border: 1px solid #434346;
}
/*очистить/восстановить форму*/

table.vertical_form > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > input:nth-child(2) {
    background-color: #8F0500!important;
    border: 1px solid #434346;
}
table.vertical_form > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > input:nth-child(3) {
    background-color: #577430!important;
    border: 1px solid #434346;
}
/*инфолоадер скрипт*/

[style="position: fixed; top: 3px; right: 3px; border: 1px dashed black; background: rgba(255, 255, 255, 0.7); padding: 3px;"],
[style="background-color:#CFFACA;"],
[class="section websection"],
[id="infoMain"],
[class="infoMailButton"] {
    background-color: #222!important;
    border: 1px dashed #434346!important;
}
#addnoteshow:hover {
    background-color: #434346!important;
}
/*кнопки*/

button,
[class="ui-state-default ui-corner-all submit"] {
    background-color: #577430!important;
    background-image: none!important;
    border: 1px solid #434346!important;
    border-radius: 4px;
    color: #F8FEDD!important;
}
button:hover,
[class="ui-state-default ui-corner-all submit"]:hover {
    background-color: #57A330!important;
    background-image: none!important;
    color: #F8FEDD!important;
}
/*удалённые чары*/

[style="background-color:#FFE0E0;"] {
    background-color: #3F1515!important;
    color: #F8FEDD!important;
}
[class="suitup-editor"] tr {
    color: #222!important;
    text-shadow: 0.5px 0px 0.5px white;
}
[class="selftest_logs"] th {
    background-color: #1E1E1E;
}
[class="separator"] {
    background-color: #1E1E1E;
}
/*красим табличку AW*/

table.tanktable tr.disabled {
    background-color: #800000;
    background-image: none;
}
/*красим заголовки в АА*/

h2,
th.tblheader {
    background: none;
    background-color: #F6A828;
    color: #1E1E1E;
}
/*красим биллинг в АА*/

.action_1 {
    background-color: #262A2B!important;
}
.action_2 {
    background-color: #523942!important;
}
.SumoSelect,
.CaptionCont,
.options,
BackdropSelect,
.CaptionCont,
.btnOk,
.btnCancel {
    background: none;
    background-color: #1E1E1E!important;
    color: #F8FEDD!important;
}
.warning-block {
    color: #1E1E1E;
}
.show-hide-control {
    background: transparent linear-gradient(rgba(250, 250, 250, 0.1), #F6A828) repeat scroll 0% 0%!important;
    height: 40px!important;
}
}
