css:
@charset "utf-8";
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain(biztok.pl) {
/*-----------------------------BIZTOK-----------------------------*/

/*chłam*/
.ads, .top__social {
display: none !important;
}
/*tło*/
.page--2 {
background : #2c2c2c !important;
background-color : #2c2c2c !important;
}
body {
background : #1C1B1B !important;
background-color : #1C1B1B !important;
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, q, blockquote, pre, 
code, var a, big, del, em, font, small, strong, sub, sup, b, u, i, dl, dt, dd, ol, ul, li,
 fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
 color: #BFBFBF !important;
 }
 .exchange, .moto-bar, .entry--1  {
 background-color: #1C1B1B !important;
background: #1C1B1B !important;
}
.business-people__content, .business-people__footer, .business-people__header {
 background-color: #858585 !important;
background: #858585 !important;
}
img {
border : 1px solid #000000 !important;
opacity: 0.6 !important;
}
img:hover {
opacity: 0.8 !important;
}
.entry__image, .scroller__image, .slideshow__promo__entry {
background: transparent !important;
}
div, class {
border-color: #4b4b4b !important;
}
/*logo*/
.site-logo { 
background-position: 0px -624px !important;
}
 /*pasek*/
 .nav {
background: #45484d!important; /* Old browsers */
background: -moz-linear-gradient(top, #45484d 0%, #1C1B1B 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#45484d), color-stop(100%,#1C1B1B))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #45484d 0%,#1C1B1B 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #45484d 0%,#1C1B1B 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #45484d 0%,#1C1B1B 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, #45484d 0%,#1C1B1B 100%)!important; /* W3C */
border-bottom: 2px solid #363636 !important;
}
.nav__item {
color: #ffffff !important;
}
.nav__item:hover {
background: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
}
.nav__item::before, .nav__item::after {
border-color: #2c2c2c !important;
}
.nav--2 {
background: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
}
.nav--2__item.selected  {
background: rgba(255,255,255,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(255,255,255,0.4),
			0 5px 10px rgba(255,255,255,0.5) !important;
color: #000000 !important;
}
.nav--2__item::after {
border-right-color: #2c2c2c !important;
}
.nav--2__item.selected::before {
border-bottom: none !important;
}
/*wyszukiwarka*/
.search__input {
background: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border: none !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
color: #B1B1B1 !important;
}
/*srodek*/
.entry--9 .entry__lead, .article__tag, .pracuj .wiecej a, .sponsored__title, .sponsored--2 .sponsored__header::before, .sponsored--2 .entry__title, .sponsored__header::before,
.sponsored__entry .entry__title  {
 background-color: #1C1B1B !important;
background: #1C1B1B !important;
 color: #BFBFBF !important;
}
.entry__label, .photo-source {
background: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border: none !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
color: #B1B1B1 !important;
}
.entry__author { 
color: #727272 !important;
}
.entry__author:hover { 
color: #ba3d3d !important;
}

/*społecznościowychlam*/
.social__views, .social__comments__box, .social__mail, .social__like {
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important; 
background: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border: none !important;
color: #BFBFBF !important;
}

/*komentarze*/
.add-comment__textarea:focus, .add-comment__nick:focus, .add-comment__textarea, .add-comment__nick {
background: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border: none !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
color: #B1B1B1 !important;
}
.button--1, .paging__prev, .paging__next {
background-color: #780000 !important;
background: #780000 !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important; 
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
}
.comments__popular {
background : #3d3d3d !important;
background-color : #3d3d3d !important;
}
/*paginator*/
.page-nr:focus {
text-decoration: underline !important;
}
.page-nr {
background: #3d3b3b !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
text-shadow: none !important;
border: none !important;
color: #d2d2d2 !important;
}

}
