css:
@-moz-document url-prefix("http://www.rabbitmq.com/"), url-prefix("https://www.rabbitmq.com/") {
body {
    background-color: #111;
    color: #0f0;
}

div#search-box {
    background: none !important;
    border-radius: 8px;
}

div#search-box, div#search-box form {
    background-color: #333 !important;
    color: #666 !important;
}

div#search-box input#search-query {
    background-color: #222;
    color: #ccc;
}

div#search-box input#search-button {
    background-color: #333 !important;
    color: #ccc;
}

table, table tbody, table tbody tr, table tbody tr td {
    background-color: #111;
    border: 0px none #111;
}

pre.PROGRAMLISTING, pre.SCREEN {
    background-color: #333;
    color: #990;
    padding: 1em;
    padding-left: 2em;
    margin: 1em;
}

pre.SCREEN {
    font-weight: bold;
    color: #cc0;
}

hr {
    border: none;
    border-top: 2px dotted #999;
}

a:link, a:visited {
    color:#4183C4 !important;
}

ul#tutorials li {
    background-color: #333;
    border: 1px solid #444;
}

div.aside, div#right-nav {
    background-color: #222 !important;
    border: 1px solid #333 !important;
    border-radius: 8px;
    color: #090 !important;
}

div#right-nav div {
    background-color: #222 !important;
}

div.aside h3, div#right-nav h4 {
    color: #999 !important;
}

div.diagram {
    background-color: #333;
    border: 1px solid #444;
    border-radius: 8px;

}

div.highlight pre, pre.sourcecode {
    background-color: #222 !important;
    color: #090;
    border: 1px solid #444;
    border-radius: 8px;
}

blockquote {
    background-color: #444 !important;
    border: 1px solid #555 !important;
}

span.code {
    color: #090; 
}

div.linenodiv {
    background-color: #444 !important;
}

div.linenodiv pre {
    color: #300 !important;
}
}
