css:
@-moz-document url-prefix("https://www.fastmail.com/mail") {
    .u-smiley {
      display: inline;
      vertical-align: baseline}
    .u-smiley--angry {background-image: none}
    .u-smiley--lol {background-image: none}
    .u-smiley--cry {background-image: none}
    .u-smiley--normal {background-image: none}
    .u-smiley--pacman {background-image: none}
    .u-smiley--sad {background-image: none}
    .u-smiley--smile {background-image: none}
    .u-smiley--surprised {background-image: none}
    .u-smiley--tongue {background-image: none}
    .u-smiley--wink {background-image: none}	
}
