@-moz-document regexp("^(https?)://yutasan\\.co/(.*)") {
/* yutasns css (beta) */

/* ========== setting ======================================== */
html {
  font-size: 62.5%;
}
body {
  margin: 0;
  padding: 0 0 20px;
  font-size: 1.2rem;
  font-family: sans-serif;
  background: #ddd;
}
header,footer,article,section,aside,div,p,h1,h2,h3,h4,h5,h6,ul,li,table,tr,th,td {
  margin: 0;
  padding: 0;
  font-size: 1.4rem;
}
ul {
  list-style: none;
}
ol {
  list-style-type: decimal;
  padding-left: 25px;
}
table {
  border-collapse: collapse;
  border: 0px;
}
img {
  border: none;
  vertical-align: middle;
}
a, a img {
  -webkit-transition: 0.3s ease;
  -moz-transition: 0.3s ease;
  transition: 0.3s ease;
  border: none;
}
a, a:link, a:visited {
  color: #03d;
  text-decoration: underline;
}
a:hover, a:active {
  color: #35c;
  text-decoration: none;
}



/* ========== menu ======================================== */
#dropmenu,
#dropmenu li ul {
  width: 100%;
  margin: 0 0 20px;
  padding: 0;
  font-size: 0.1rem !important;
  background: #000;
}
#dropmenu li,
#dropmenu li ul li {
  display: inline-block;
  position: relative;
  width: auto;
  max-width: 200px;
  min-width: 70px;
  margin: 0;
  padding: 0;
  text-align: left;
}
#dropmenu li a {
  display: block;
  margin: 0;
  padding: 1.5rem 1rem 1.5rem 3.2rem;
  color: #fff !important;
  line-height: 1;
  font-size: 1.2rem;
  text-decoration: none !important;
  border-radius: 0 !important;
}
ul#dropmenu > li:nth-child(1) a {
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-position: 1rem center;
  background-size: auto 1.6rem;
}
ul#dropmenu > li:nth-child(2) a {
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-position: 1rem center;
  background-size: auto 1.6rem;
}
ul#dropmenu > li:nth-child(3) a {
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-position: 1rem center;
  background-size: auto 1.6rem;
}
ul#dropmenu > li:nth-child(4) a {
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-position: 1rem center;
  background-size: auto 1.6rem;
}
#dropmenu li:hover a {
  background-color: #555;
}
#dropmenu li ul li a {
  background-image: none !important;
}
#dropmenu li ul{
  position: absolute;
  top: 100%;
  left: 0;
  margin: 0;
  padding: 0;
}
#dropmenu li:last-child ul{
  left: 0%;
  width: 100%
}
#dropmenu li ul li{
  display: block;
  overflow: hidden;
  width: 100%;
  height: 0;
  color: #fff;
  -moz-transition: .2s;
  -webkit-transition: .2s;
  -o-transition: .2s;
  -ms-transition: .2s;
  transition: .2s;
}
#dropmenu li:last-child ul{
  top: 0;
  left: 0;
  width: 100%
}
#dropmenu li:hover ul li{
  overflow: visible;
  height: 3rem;
  border-top: 1px solid #ccc;
}
#dropmenu li:hover ul li:first-child{
  border-top: 0;
}
#dropmenu li:hover ul li:last-child{
  border-bottom: 0;
}
#dropmenu li ul li a{
  padding: 1.2rem;
  background-color: #333 !important;
  font-weight: normal;
}
#dropmenu li ul li:hover a{
  background-color: #555 !important;
}
  
@media screen and (max-width: 479px) {
  #dropmenu li,
  #dropmenu li ul li,
  #dropmenu li a {
    font-size: 1rem;
  }
}
    
/*----------------------------------
   menu.css reset [[ start
--------------------------------- */
