  #dropmenu li,
  #dropmenu li ul li,
  #dropmenu li a,
  #dropmenu li ul li a {
    font-size: 1rem !important;
  }
}
/*----------------------------------
   end ]] menu.css reset
--------------------------------- */



/* ========== waterfall ======================================== */
div#inputPoint + .posts tr td {
  padding: 10px !important;
}
div#inputPoint + .posts tr td:first-child,
div#inputPoint + .posts tr td form textarea + br,
div#inputPoint + .posts tr td form input + br {
  display: none !important;
}
div#inputPoint + .posts tr td form input {
  margin: 5px 0;
}
div#inputPoint + .posts tr td form input[id="sendbtn"] {
  padding: 10px 20px;
  border-radius: 100px;
  color: #fff;
  background: #000;
}
@media screen and (max-width: 1272px) {
  div#inputPoint + .posts {
    width: 94%;
    margin: 0 3% !important;
  }
}

div#viewPoint,
div#oldViewPos {
  margin-top: 1em;
  padding: 0 3%;
}

.posts {
  width: 100%;
  max-width: 1200px;
  margin: 0 auto 1em;
  background: #ffffff;
  border: none;
  border-radius: 8px;
  box-shadow: 0 0 10px 0 rgba(0,0,0,0.1);
}
.posts tr td {
  padding: 0 10px 10px 3px;
  background: #ffffff;
  border: none;
  border-radius: 8px;
}
.posts tr:first-child td {
  padding-top: 10px;
}
.posts tr td:first-child {
  padding: 10px 0 10px 20px;
  min-width: 32px;
  font-size: small;
  text-align: center;
  word-break: break-all;
}
.posts tr:first-child td:first-child {
  padding: 10px;
}
@media screen and (max-width: 640px) {
  .posts tr td:first-child {
    font-size: x-small;
  }
}
.posts tr td:first-child a .imageCircle {
  border-radius: 64px;
  max-width: 64px;
  width: 100%;
  height: auto;
}
.posts tr td:first-child a:hover .imageCircle {
  opacity: 0.8;
  box-shadow: 0 0 20px 0 rgba(61,92,196,1);
}
.posts tr td textarea {
  width: 95%;
  padding: 5px;
  border: solid 1px #ccc;
}

.posts tr td .arrow_box {
  position: relative;
  margin-left: 10px;
  padding: 10px;
  border: solid 2px #ccc;
  border-radius: 5px;
  line-height: 1.2;
  word-break: break-all;
  background-color: #eee;
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-position: right bottom;
  background-size: auto 2.5em;
}
.posts tr:first-child td .arrow_box {
  margin-left: 0;
  background-image: none;
  background-color: #fff;
}
.posts tr td .arrow_box:before,
.posts tr td .arrow_box:after {
	right: 100%;
	top: 50%;
	border: solid transparent;
	content: " ";
	height: 0;
	width: 0;
	position: absolute;
	pointer-events: none;
}
.posts tr td .arrow_box:before {
  border-color: none;
  border-right-color: #ccc;
  border-width: 10px;
  margin-top: -10px;
}
.posts tr td .arrow_box:after {
  border-color: none;
  border-right-color: #eee;
  border-width: 7px;
  margin-top: -7px;
}
.posts tr:first-child td .arrow_box:after {
  border-right-color: #fff;
}
.posts tr td .arrow_box img {
  width: auto;
  max-width: 100%;
  height: auto;
}
@media screen and (max-width: 360px) {
  .posts tr td .arrow_box img {
    width: 100%;
  }
}

.posts tr td .arrow_box a[href^="javascript:snsButton"] {
  display: inline-block;
  margin: 0 5px 5px 0;
  padding: 4px 10px;
  border-radius: 4px;
  color: #fff;
  font-size: small;
  text-decoration: none;
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-position: center center;
  background-size: 100%;
}

.posts tr td .arrow_box a[href^="/search/tag"] {
  display: inline-block;
  margin: 0 0.2rem;
  padding: 0.2rem 0.4rem;
  border-radius: 3px;
  background: rgba(0,0,0,0.1);
  color: #888;
  color: rgba(0,0,0,0.5);
}
.posts tr td .arrow_box a[href^="/search/tag"]:hover {
  background: rgba(0,0,0,0.2);
  color: #666;
}

.posts tr td .arrow_box font[color$="c0c0c0"] {
  display: block;
  margin: 5px 0;
  padding: 5px 0;
  border-top: solid 1px rgba(0,0,0,0.1);
  border-bottom: solid 1px rgba(0,0,0,0.1);
  color: rgba(0,0,0,0.2);
  font-size: 1rem;
}

.posts tr td .arrow_box font[color$="c0c0c0"] + br {
  display: none;
}

.posts tr td .arrow_box a[href^="javascript:reSend"] {
  display: inline-block;
  margin: 0;
  padding: 3px 6px 3px 1.7em;
  border-radius: 4px;
  background: #ccc;
  color: #000;
  font-size: small;
  text-decoration: none;
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-position: 0.5em center;
  background-size: auto 1em;
}

input[type="hidden"] + br,
table[class="posts"] + br {
  display: none;
}

.posts tr td .arrow_box a[href^="javascript:snsButton"]:hover {
  -webkit-animation: shake-base 100ms infinite ease-in-out;
