css:
@-moz-document domain(drive2.ru) {
.user-maininfo .nickname {
    margin: 5px 5px 9px 0px;
    font: 15px/1.3 sans-serif;
    color: gray;
}
.round-num-block {
    display: inline-block;
    width: 76px;
    height: 76px;
    margin: 0px 0px 0px 20px;
    border: none;
    border-radius: 40px 40px 40px 40px;
    color: gray;
    font: 12px/1 sans-serif;
    text-align: center;
    cursor: pointer;
    vertical-align: bottom;
}
.car-caption {
    margin: 0px 0px 15px;
    color: gray;
    font: 18px/1 HelveticaNeue,"Helvetica Neue",sans-serif;
}
.xbutton-big.subs-button {
    width: 160px;
    margin-right: 4px;
}
.car-desc {
    font: 13px/1.3 sans-serif;
    word-wrap: break-word;
}
.h2, h2 {
    margin: -3px 0px 0px;
    font: 16px/1 HelveticaNeue,"Helvetica Neue",sans-serif;
}
h3 {
    margin: 0px 0px 15px;
    font: 16px/1 sans-serif;
}
.comment-text {
    font: 13px/1.3 sans-serif;
    word-wrap: break-word;
}
.vcard-username {
    font: bold 14px/1 sans-serif;
}
.xbutton-big {
    height: 21px;
    font-size: 12px;
    border-width: 0px;
}
.maininfo {
    position: relative;
    padding: 5px;
    color: gray;
}
.car-brandnav {
    margin: -5px 0px 1px;
    font: 14px/1.2 sans-serif;
    color: gray;
    word-wrap: break-word;
}

.car-brandnav a {
    color: gray;
}

.journal-entry-body {
    font: 13px/1.3 sans-serif;
    word-wrap: break-word;
}
.xbutton {
    position: relative;
    display: inline;
    height: 22px;
    padding: 0px 23px 1px;
    color: gray;
    font: 13px/18px sans-serif;
    outline: 0px none;
    text-decoration: none;
    cursor: pointer;
    white-space: nowrap;
    text-align: center;
    background: none repeat scroll 0% 0% transparent;
    border: 1px solid gray;
    -moz-box-sizing: border-box;
    border-radius: 5px 5px 5px 5px;
    vertical-align: middle;
}
.user-num-blocks {
    position: absolute;
    right: 0px;
    top: -12px;
}
.user-maininfo {
    position: relative;
    z-index: 2;
    padding: 20px;
    background: none;
    overflow: hidden;
}
.round-num-block strong {
    display: block;
    color: gray;
    margin: 20px 0px 0px;
    font: 12px/1 sans-serif;
}
.user-maininfo .vcard-note-imp, .user-maininfo .vcard-virtual-car {
    font: 13px/1.2 sans-serif;
    color: gray;
}
.user-maininfo .vcard-note {
    color: gray;
    font: 11px/1.6 sans-serif;
}
.l-top {
    width: 960px;
    min-height: 50px;
    margin: 0px auto;
    background: none repeat scroll 0% 0% rgb(18, 21, 25);
}
.user-maininfo .vcard-username {
    position: relative;
    top: 0px;
    line-height: 16px;
    color: rgb(255, 255, 255);
}
.user-ministats, .user-ministats a {
    font-weight: normal;
    color: gray;
    position: absolute;
    top: 60px;
}
.maininfo, .mainpic {
    background: none;
}
.car-caption a {
    color: gray;
}
.logbook-list-row-caption {
    font: 13px/1 sans-serif;
}
.car-buttons {
    float: left;
    margin: -12px 0px 0px;
}
.car-num-blocks {
    float: right;
    margin: -45px 0 0 0;
}
.logbook-list-title {
    font: 13px/1 sans-serif;
    color: gray;
    white-space: nowrap;
}
.journal-entry-title {
    word-wrap: break-word;
    margin-bottom: 12px;
}
.journal-entry-pic-desc {
    display: block;
    position: relative;
    left: 10px;
    width: 800px;
    margin: 4px 0px 0px;
    font: 12px/1.4 sans-serif;
    color: rgb(102, 102, 102);
}
.like-counter {
    display: none;
}
.car-bookmarks-button {
    position: absolute;
    top: 0px;
    right: -5px;
}
.carcard-fhd .carcard-drive {
    display: none;
}
.carcard-fhd {
    font: 14px/23px sans-serif;
    margin-top: 10px;
}
.carcard {
    background: none;
}
.carcard a {
    color: gray;
}
.carcard-body {
    color: gray;
    background: white;
}
.car-num-blocks {
    display: none;
}
.top-nav {
    font: 13px/50px HelveticaNeue,"Helvetica Neue",sans-serif;
}
.msg {
    font: 12px/1.4 sans-serif;
}
.top {
    color: rgb(51, 51, 51);
    background: #eee;
}
.carcard-drive {
    display: none;
}
.counter {
    display: none;
}
.postitem-caption {
    font: 14px/23px sans-serif;
}
.postcard-caption {
    font: 13px/18px sans-serif;
}
.postitem-lead {
    color: rgb(51, 51, 51);
    font: 13px/21px sans-serif;
}
.top-nav-link, .top-nav-sub {
    color: rgb(51, 51, 51);
}
}
