css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("we.easyelectronics.ru") {
#sidebar,
#footer,
.content a{
display: none;
}
  
#container,
#wrapper,
#content,
#content-inner,
#header,
#nav,
#nav-inner,
.topic{
    color: white;
    background: black;
    margin-left: 0;
    margin-right: 0;
    margin-top: 0;
    margin-bottom: 0;
    padding-left: 0;
    padding-right: 0;
    padding-top: 0;
    padding-bottom: 0;
}

#content-inner .topic ul.info{
  background: #202020;
}
  
li.username a{
  color: #0040c0;
}
  
h1.title a{
  color: #00c0ff;
  font-weight: bold;
}
  
.topic{
    width: 1200px;
    margin-left: 0;
    margin-right: 0;
    margin-top: 0;
    margin-bottom: 0;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 0;
    padding-bottom: 0;
}
  
.topic .info li.comments-link a{
  color: #0f0;
  }
}
