css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gyao.yahoo.co.jp") {
  /* メニューバーを隠す 
      カテゴリ移動は面倒になる */
  #left_wrapper.waku{
    display: none !important;
  }
  /* 検索ボックスを隠す */
  #right_header{
    display: none !important;
  }
  /* 本体の内容を以って、隠した要素を埋める */
  #contents_wrapper /* GyaO!トップページ */
  ,#subcontents_wrapper /* 各カテゴリーページ */ {
    position: relative;
    left: -240px; /* 左のメニューバーの分。 */
    top: -70px; /* 検索ボックスの分 */
  }
}
