css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("photobucket.com") {
/*BG*/
body {
    background-color: #000;
    color: #0f0;
}
/*navbar*/
.navbar .subnav {
    background-color: #000;   
}
/*Footer*/
#footer .siteLinks {
    display: none;
}
/*start page container*/
.topContainer {
    background-color: #000;
    border: 1px solid #0f0;
}
.welcomeMsg {
    color: #0a0;
}
.footerOuter {
    display: none;
}
#footer {
    display: none;
}
/*browse more button*/
.categories #browseButton {
    background-color: #000;
    border: 1px solid #0f0;
    color: #0a0;
}
/*next button*/
a#btnnext.lastChild.btn.btn-small.btn-primary {
    background-color: #010;
    color: #0f0;
}
/*prev button*/
a#btnprev.btn.btn-small.btn-primary {
    background-color: #010;
    color: #0f0;
}
/*hyperlinks*/
a {
    color: #0a0;
}
/*focused*/
a:hover {
    color: #0f0;
}
/*image box*/
#mediaTemplateTarget {
    background-color: #000;
    border: 1px solid #0a0;
}
.mediawrapper table td {
    background-color: #000;
}
/*overlay buttons*/
/*previous*/
#mediaTemplate #previous {
    opacity: 0.1;
}
#mediaTemplate .previous {
    background: #010;
}
/*next*/
#mediaTemplate #next {
    opacity: 0.3;
}
#next.imageController {
    
}
#mediaTemplate .next {
    background: #010;
}
/*input search bar*/
.subnavSearch .navbar-search .inputContainer .search-query {
    border-radius: 1px;
    border: 1px solid #0f0;
    border-right: 1px solid #0f0;
    background-color: #000;
    color: #0f0;
}
/*test*/

}
