css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('liginc.co.jp')
{
    .bg_jack,
    .u_frame_main,
    .l_header,
    .l_bread_up,
    .l_footer,
    .b_sns_horizon,
    .b_table_media,
    .l_side_left,
    .l_side_right,
    .info,
    iframe,
    .b_info_in_article,
    .b_info_c_frame,
    .l_navi,
    .category_bg,
    .l_category_header,
    .b_sns_lig_page,
    .info_mailmaga,
    .sns_follow_box,
    #js-fixed-header,
    .company_nav
    {
        display: none;
    }
    body
    {
        background: #f1f1f1;
    }
    .l_contents
    {
        width: 840px !important;
        min-width: 655px !important;
        margin: 3em auto !important;
        padding: 30px;

        background: #fff;
        box-shadow: 0 0 10px #999;
    }
    .l_contents .b_contents
    {
        width: 780px !important;
        margin: 0 !important;
        padding: 0 !important;
    }
    .l_main
    {
        width: 780px !important;
        margin: 0;
    }
    .l_wrapper{
        min-width:100%;
    }
}
