/*****************************************************************************************
*
*    				Globals
*
*******************************************************************************************/
  
  p,a{font-family: DejaVu Sans, Verdana, Geneva, sans-serif;}
  p{color: #333 !important;}
  .post-content p a:not(.tag),
    .comment-content a:not(.tag){
  text-decoration:none;
  border-bottom:1px dotted #6A5687;
  color:#6A5687;
}

  .post-content p a:hover, a:focus {
  border-color:#583C66;
  color:#583C66;
  text-decoration:none;
  border-bottom-style:solid;
}
  #user_menu a{color:#6A5687;text-shadow: none !important;font-weight:bold !important;}
  #user_menu a:hover,#user_menu a:focus{color:#D2703A;text-decoration:none;background: none !important;}

/*****************************************************************************************
*
*    				Header
*
*******************************************************************************************/
/*body{background:#DFDFDF !important;}*/
header, header #user_menu,body > header .header-nav > span{background:#F8F8F8 /* #333 */ !important;box-shadow:none !important;border: none !important;}
body > header .header-nav a{font-size: 1.4em !important;color: #6A5687 !important;}
body > header .header-nav a:hover{color:#D2703A !important;}
body > header ul#user_menu > li{border: none !important;}
body > header .diaspora_header_logo{visibility:hidden !important;}
body > header:after{content:""; background: url("data:image/png;base64,") !important;width: 40px !important;height: 40px !important; display: block !important;margin-top:-32px !important;margin-left:20px !important;}    
body > header #nav_badges .badge{font-size: 1.4em !important;}
body > header #global_search form input{border-radius:5px !important;background: #f8f8f8 !important;border: 1px solid #D2703A !important;box-shadow:none !important;}
body > header #nav_badges .badge .badge_count{font-size: 1em !important;background:transparent !important;color: #D2703A !important}
body > header .user-menu-avatar{border-radius: 50% !important;}
.avatar{border-radius: 50% !important;border: 1px solid #D2703A !important}
  
.icons-notifications_grey{background: url('http://v.sanspseudofix.fr/userstyles/diaspora/alarm8.png') !important;width: 18px !important; height: 16px !important;background-position: 0px 0px !important;background-repeat: no-repeat !important;margin-top: 10px !important;}
.icons-mail_grey{background: url('http://v.sanspseudofix.fr/userstyles/diaspora/mailbox1.png') !important;width: 16px !important; height: 22px !important;background-position: 0px 0px !important;background-repeat: no-repeat !important;}
  
#home_user_badge h4{display:inline-block !important;margin-left: -60px !important;margin-top: 60px !important; overflow:visible !important;width:100% !important;}
#home_user_badge h4 a{display:block !important;width:100% !important;overflow:visible !important;word-wrap:break-word !important;}
/*****************************************************************************************
*
*					Body
*
*******************************************************************************************/

.span-24{width: 100% !important;}
.span-13,.span6{width:69% !important;} /* span-13 for stable and span6 for dev */
.container{width: 100% !important;}
#leftNavBar{width:10% !important;/*float:left !important;position:absolute !important;*/}
.rightBar{width: 15% !important;/*margin-top: 260px !important;float:left !important;position:absolute !important;*/}
.stream_container{border-left: none !important;}
.post-content .reshare{background: #f8f8f8 !important;border-left:3px solid #000 !important;border-radius:0px 5px !important;}
.post-content .reshare>.media:before{content:'';display: block !important;position:absolute !important;margin-left:-60px !important;margin-top: 30px !important;background: url('http://v.sanspseudofix.fr/userstyles/diaspora/social105.png') no-repeat !important;width:32px !important;height:32px !important;/*background-position:-35px -35px !important;*/border-radius: 24px !important;}
.feedback .reshare{border-left: none !important;}
#single-post-content,#single-post-interactions{width: 48.7179% !important;}
#tags_list{background: #f8f8f8 !important;z-index: 200 !important;position: absolute !important;}
/*********************** Comment ***********************/
.comment{border-bottom: none !important;}
.comment.media{border-top: none !important;}
.show_comments:before{visibility:hidden !important;}
.comments .comment .bd,
#single-post-interactions .comments .comment .bd{background: #fff !important;border:1px solid #D2703A !important;padding: 5px !important;}
.comment_stream .comments .comment:before{content:"";display:block !important;position:relative !important;width:0px !important;height:0px !important;left:32px !important;top:20px !important;border-right:11px solid #333 !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
#single-post-interactions .comments .comment:before{content:"";width:0px !important;height:0px !important;display:block !important;position:relative !important;left:37px !important;border-right:10px solid #D2703A !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
#profile_container .comments .comment:before{content:"";width:0px !important;height:0px !important;display:block !important;position:relative !important;left:32px !important;border-right:11px solid #D2703A !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
.new_comment_form_wrapper:before{content:"";visibility: hidden !important;display:block !important;position:absolute !important;left:21.9% !important;border-right:11px solid #D2703A !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
.stream_element .collapsible p{color: #333 !important;}
.collapsible.comment-content > p{color: #333 !important;}
.likes img{background: none !important;padding: 0 !important;}
#tags_show, #tags_shwo .row-fluid{width:100% !important;}
.container-fluid{padding: 0px !important;}
#tags_show .span3{width:20% !important;}
.row-fluid .offset1:first-child{margin-left: 0 !important;}
#tags_shwo .row-fluid{width:100% !important;}
.container-fluid .comment_stream .comments .comment:before{content:"";display:block !important;width:0px !important;height:0px !important;position:relative !important;left:33px !important;border-right:10px solid #333 !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
.comments .new_comment_form_wrapper .bd{border:0px solid transparent !important;}
.stream_element .new_comment_form_wrapper .comment_box{width:99% !important;}
.stream_element .new_comment_form_wrapper{width:70% !important;}
/*********************** /Comment ***********************/
  
#back-to-top{background: url("data:image/png;base64,") !important;width: 40px !important;height: 40px !important; display: block !important;margin-top:-32px !important;margin-left:20px !important;background-repeat: no-repeat !important;}
  
}
