css:
@-moz-document url-prefix("https://www.fanduel.com") {
h1, h2, h3, h4, h5, h6, legend {
  color: lightgray;
}

body {
  background: #222 !important;
}

section.container {
  background: black;
  color: lightgray;
}

h1 {
  color: orange;
}

.boxed-group {
  background: #444;
}

.button.primary, button.primary {
  background: orange;
  border-color: #644;
    }

.global-header {
  border-color: orange;
}
      
.button.mini.fixture {
  padding: 3px;
  background: none;
  font-size: 8pt;
          border: 0px;
          word-spacing: -3px;
  width: 50px;
          height: 60px;
 }
.button.mini.fixture small {
    display:block;
          word-spacing: 0px;
                font-size: 6pt
              }
input[type=text], input[type=password], input[type=email], input[type=search], select, select[multiple], textarea {
    background: #444;
    color: white;
  }
  
.alert.bg-blue3 {
    background: #333 !important;
 }
      
.alert {
    line-height: 1.2em;
    padding-left: 23px !important;
    border-radius: 5px!important;
    border: 1px solid white;
  }
  
.alert i {
  position: absolute;
      left: 5px;
  }

.navigation-logged-in .navigation-logged-in-menu a.is-active {
      box-shadow: inset 0 4px 0 0 orange;
    }

}

@-moz-document url-prefix("https://www.fanduel.com/e/Game") {
.contest-player-list-container .player-list-position-tabs li a {
      width: 3em;
      background: #222;
      text-align: center;
      padding-left: 0px;
      padding-right: 0px;
      border-color: #444;
    }
    
.contest-player-list-container .player-list-position-tabs li a.is-active {
          border-color: orange;
          background: #444;
        }

.roster-container {
  border: 1px solid white;
  border-radius: 5px;
  padding: 5px 8px 0px;
  background: #333;
}
  
.roster-container .roster {
  border: 0px;
  padding: 0px;
}
      
.roster-container .roster ul li {
  background-color: #333;
}
          
.roster-container footer {
  border: 0px;
  border-top: 1px solid white;
  border-radius: 0px;
  background: none;
  margin: 0px -8px;
}

.fixture-picker .fixture-picker-button-container, 
.fixture-picker .fixture-picker-button-container a.button.mini {
  background: #333;
}
    
.fixture-picker .button.mini, .fixture-picker .button.mini:first-child.is-active {
  border-top: 3px solid #333;
            
}.fixture-picker .button.mini.is-active, .fixture-picker .button.mini:first-child.is-active {
  background: #333;
  border-top: 3px solid orange;
}
        
.contest-player-list-container .legend .draft-legend-key-title,
.contest-player-list-container .legend .draft-legend-key-title.is-expanded
{
  border: 0px;
}
        
.contest-player-list-container .legend, 
.contest-player-list-container .legend .draft-legend-key-content,
.contest-player-list-container .legend .draft-legend-key-title.is-expanded{
  background: none;
}

.player-list-table .player-in-lineup, .player-list-table-header .player-in-lineup, .roster-table .player-in-lineup {
        background: #444;
}
      
.contest-message {
    background: none;
    line-height: 1.2em;
    padding-left: 23px;
    border-radius: 5px!important;
    border: 1px solid white;
    margin-top: 8px;
    background: #333;
  }
  
.contest-message i {
  position: absolute;
      left: 5px;
  }

.fixture-picker:after {
      background: none;
    }
}

@-moz-document url-prefix("https://www.fanduel.com/contest/") {
.roster-contest-information {
    background: none;
  }
  
.game-metadata {
      background: #333;
      color: lightgray;
    }

.game-metadata .button-transparent-bg {
  color: lightgray;
  border-color: lightgray;
}
    
h2 {
      color: lightgray;
    }

.roster-row, .roster-row.finished {
    background: #222;
  }
  
  .roster-row.active {
    background: #444;
      }
.roster-row .name, .roster-row.active .fixture-info {
   color: lightgray;
          }
.slot {
   background: none;
            }
            
#table-meta-information {
              background: #333;
              border: 1px solid gray;
            }
.fixture-card {
  border: 1px solid lightgray;
    border-width: 0px 1px 0px 0px;
    margin: 0px;
    border-radius: 0px;
    box-shadow: none;
  background:none;
  }
.fixture-card:after {
  background:none;
  }
  
#live-scoring-fixture-info section>ul {  
    background: #333;
  height: auto;
  padding: 0px;
  border: 1px solid gray;
  }

#live-scoring-fixture-info section {
  height: auto;
}

#live-scoring-fixture-info {
  margin-bottom: 0px;
}
  
#live-scoring-leaderboard table.condensed {
  background: #333;
  border: 1px solid gray;
  border-radius: 5px;
  margin-top: 7px;
}

#live-scoring-leaderboard table tbody tr:nth-of-type(odd) {
  background: none;
}

#live-scoring-leaderboard table tbody tr td {
  border-color: #444;
}

#live-scoring-leaderboard table tfoot tr {
  background: none;
}

.roster-row.finished .score {
  background: #222;
  color: gray;
}

.user-score-summary .rank, .user-score-summary .score {
  background: #000;
  color: lightgray;
  border: 1px solid gray;
}
}

@-moz-document url-prefix("https://www.fanduel.com/p/Home") {
.featured-ads-iframe {
  height: 130px;
  border-bottom: 1px solid gray;
  overflow: hidden;
}
  
div.lobby div.filter {
    box-shadow: none !important;
    padding-right: 8px;
  }
  
div.lobby div.filter div.sport label {
    width: 60px;
  }
  
div.lobby table thead {
  border: 0px !important;
}
div.lobby table thead tr {
  display: -webkit-flex;
  display: flex;
  -webkit-flex-direction: row;
  flex-direction: row;  

  position: absolute;
  right: 5px;
    left: 0px;
  z-index: 1000;
  margin: 2px 10px 0px 2px;
  border: 0px !important;
  background: none !important;
  }

div.lobby table thead tr th{
    background: black;
  border-bottom: 2px solid #333 !important;
  border-width: 0px 0px 2px !important;
  color: lightgray;
  height: 45px;
  padding-top: 15px !important;
}

div.lobby table thead tr th:nth-of-type(1) {
	border-radius: 8px 0px 0px 0px;    
  flex: 3;
    }
  
div.lobby div.items table.items tbody tr {
  background: none;
  color: white;
  border: 0px 0px 3px solid gray !important;
}

div.lobby div.items table.items tbody tr td {
  border-bottom: 1px solid #333 !important;
}

div.lobby div.items table.items tbody tr td.sport img {
  -webkit-filter: invert(100%);
}

div.lobby div.items table.items tbody tr:nth-child(2n) {
  background: none;
}
    
div.lobby table.headers, div.lobby table.headers tr th {
  border: 0px;
}

div.lobby div.items table.items tbody tr, div.lobby div.items table.items tbody tr td {
      border-bottom: 0px;
    }
    
div.lobby div.items div.body .updatesAvailable {
  background: #333;
  color: orange;
  position: absolute !important;
  z-index: 999999;
  left: 5px;
  width: 40%;
          top: 10px;
  height: 25px;
          padding: 1px 10px;
  border: 1px solid lightgray;
  border-radius: 5px;
  
}

div.lobby div.filter {
  background: black;
  color: lightgray;
}

div.lobby div.filter div.selector label{
background: linear-gradient(to bottom,#000 0,#272727 100%);
  border: 1px solid #222;
  color: #aaa;
}

div.lobby div.filter div.selector label.checked {
  border-bottom: 1px solid orange;
  color: orange;
}

div.lobby div.filter div.selector label.disabled {
  color: #444;
}
    
div.lobby div.title {
      background: none; 
}
    
div.lobby div.title h1 {
      display: none; 
}
    
div.lobby div.items div.body div.inner {
      border: 2px solid gray;
      border-right: 0px;
      border-radius: 10px;
      background: none !important;
      padding-top: 45px;
    }
    
div.lobby div.items div.body div.inner::-webkit-scrollbar {
      background: none !important;
		border: 2px solid gray !important;
      padding-top: 5px;
    }
    
div.lobby div.items div.body div.inner::-webkit-scrollbar-thumb {
  border-radius: 15px !important;
  border: 1px solid #644 !important;
  background-color: orange !important;
}
    

}
